import { Component, Inject, OnInit, ViewChild } from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ClaimService } from 'app/claim/service/claim.service';
import { AccountService } from 'app/core/auth/account.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { Table } from 'primeng/table';
import { HttpParams } from '@angular/common/http';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { SaveLetterItemsDto } from '../model/save-letter-items-request.model';
import { SEClaimItemDto } from 'app/claim/se-claim/model/se-claim-item.model';
import { PopUpService } from '../../service/pop-up.service';

@Component({
  selector: 'n2-submit-letter-status',
  templateUrl: './submit-letter-status.component.html',
  styleUrls: ['./submit-letter-status.component.scss']
})
export class SubmitLetterStatusComponent implements OnInit {

  params: any;
  regroupArr: any[];
  loading!: boolean;
  selectAll = false;
  submitLetterItemDto: SaveLetterItemsDto = {} as SaveLetterItemsDto; // main persisting request

  public selectedRows: any[] = [];

  rejectDropDownList: any[] = [{ name: 'Closed (No Reply)', key: 'CNR' }, { name: 'Closed (NSM Req)', key: 'CNS' }, { name: 'Closed (Coy Req)', key: 'CCO' },
  { name: 'Closed (Payroll)', key: 'CPY' }];


  submitLetterStatusForm: FormGroup = this.formBuilder.group({
    claimArr: new FormArray([]),
    captionGroup: this.formBuilder.group({
      itemStatus: new FormControl(null, null)
    }),
  });
  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private formBuilder: FormBuilder,
    private claimService: ClaimService,
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private popUpService: PopUpService,

  ) {
    console.log("init submit-letter-status")

  }



  ngOnInit(): void {
    console.log("init submit-letter-status")
    this.route.queryParams.subscribe((params) => {
      this.params = params;
    });
    this.regroupArr = JSON.parse(this.route.snapshot.paramMap.get("regroupArr"));
    this.submitLetterItemDto = JSON.parse(this.route.snapshot.paramMap.get("submitLetterItemDto"));
    this.initClaimTable();

  }

  get captionGroup(): FormGroup {
    return this.submitLetterStatusForm?.get('captionGroup') as FormGroup
  }

  get claimArr(): FormArray {
    return this.submitLetterStatusForm?.get('claimArr') as FormArray;
  }

  onCancel(): void {
    console.log("cancel")
    console.log("the megala", this.submitLetterStatusForm);
  }

  initClaimTable(): void {
    console.log("rusa", this.regroupArr)
    this.regroupArr.forEach(claimObj => {
      this.claimArr.push(this.addRow(claimObj));
    })
    //on initializing, preselect all items with PVE
    this.preselectPVEItems();
    if (this.params.letterTypeCode === 'S06' || this.params.letterTypeCode === 'S10' || this.params.letterTypeCode === 'E04') {
      this.submitLetterStatusForm.get('captionGroup.itemStatus').setValue('PIN');
    } else if (this.params.letterTypeCode === 'S14' || this.params.letterTypeCode === 'E09' ) {
      this.submitLetterStatusForm.get('captionGroup.itemStatus').setValue(this.rejectDropDownList[0].key);
    }
  }


  addRow(claimObj: any): FormGroup {

    const fg: FormGroup = this.formBuilder.group({
      display: new FormControl(claimObj.display, null),
      verifier: new FormControl(claimObj.verifier, null),
      verifierInit: new FormControl(claimObj.verifierInit, null),
      trgStartDte: new FormControl(claimObj.trgStartDte, null),
      trgEndDte: new FormControl(claimObj.trgEndDte, null),
      itemList: this.formBuilder.group({})
    });
    (claimObj.itemList as any[]).forEach((x: any) => {
      (fg.get('itemList') as FormGroup).addControl(x.claimItemId, this.addItemRow(x));
    })
    return fg;

  }
  addItemRow(x: any): FormGroup {
    const itemObj = {
      itemName: new FormControl(x.itemName, null),
      latestItemStatusDesc: new FormControl(x.latestItemStatusDesc, null),
      claimItemId: new FormControl(x.claimItemId, null),
      latestItemStatusCode: new FormControl(x.latestItemStatusCode, null)
    }
    return this.formBuilder.group(itemObj)
  }

  getInnerArrayFormControl(fa: FormArray, index: number, innerControlName: string): any {
    return (fa.controls[index].get(innerControlName) as FormGroup);
  }

  getInnerArrayFormController(fa: FormArray, index: number, innerControlName: string): AbstractControl<any, any>[] {
    const formCtrlArr: AbstractControl<any, any>[] = [];
    const fg = this.getInnerArrayFormControl(fa, index, innerControlName) as FormGroup;
    Object.keys(fg.controls).forEach(key => {
      formCtrlArr.push(fg.get(key));
    })
    return formCtrlArr;

  }

  preselectPVEItems() {
    const keyList: string[] = [];
    let fg: FormGroup;
    this.claimArr.controls.forEach(ctrl => {
      fg = ctrl.get('itemList') as FormGroup;
      Object.keys(fg.controls).forEach(key => {
        if (fg.get(key).get('latestItemStatusCode').value === "PVE") {
          keyList.push(key);
        }
      });
    })
    this.selectedRows = keyList;
  }

  onSelectAllChange(event: any): void {
    const keyList: string[] = [];
    let fg: FormGroup;
    this.claimArr.controls.forEach(ctrl => {
      fg = ctrl.get('itemList') as FormGroup;
      Object.keys(fg.controls).forEach(key => {
        keyList.push(key);
      });
    })
    const checked = event.checked;
    if (checked) {
      this.selectedRows = keyList;
      this.selectAll = true;
    } else {
      this.selectedRows = [];
      this.selectAll = false;
    }
  }

  tester(): void {
    console.log("the mega", this.submitLetterStatusForm)

    console.log("the selected", this.regroupArr)
    console.log("main  persist", this.submitLetterItemDto);
    console.log("the real  select", this.selectedRows)
  }

  openDialog(event: Event, type: string): void {
    console.log("adding letter items")
    this.nspConfirmService.onConfirmPopup(event, type, null,
      () => {
        switch (type) {
          case 'changeStatus':
            this.changeStatus();
            break;

        }
      }
    );
  }

  changeStatus() {
    console.log("confirm change status");

    this.submitLetterItemDto.claimObjList.forEach(claimObj => {
      //set claim item dto to claimitemlist
      const itemList = this.regroupArr.find(x => x.caseId === claimObj.caseId).itemList as any[];
      const claimItemDtoList: SEClaimItemDto[] = [];
      itemList.forEach(y => {
        //retain only selected claim items
        const claimItemDto: SEClaimItemDto = {
          id: y.claimItemId,
          claimId: y.claimId,
          itemName: y.itemName,
          latestStatus: this.submitLetterStatusForm.get('captionGroup.itemStatus').value,
        }
        claimItemDtoList.push(claimItemDto);
      })
      claimObj.claimItemList = claimItemDtoList.filter(claimItem => this.selectedRows.includes(claimItem.id));
    })
    console.log("the final", this.submitLetterItemDto);

    this.claimService.submitClaimLetter(this.submitLetterItemDto, null).subscribe((resp: any) => {
      if (resp.status === 200 && resp.body.data) {
        this.toastService.success(String(resp.body.success_message));

        this.navigateClaimLetter();
      }
    },
      (error: any) => {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(String(error?.error?.error_desc));
      });
  }

  navigateClaimLetter(): void {
    const route = JSON.parse(window.localStorage.getItem('claim-letter-route'));
    const queryParams: any = route.queryParams;
    this.router.navigate(['claim/pop-up/claim-letter', route.snapshotParams], { queryParams })

  }
}
<form [formGroup]="submitLetterStatusForm">
    <div class ="upper-form">

        <div class = "grid col-12" [style]="{'margin-bottom':'1em'}">
            <h5>{{ params?.caseId }} {{ params?.letterTypeTitle}} </h5>
        </div>
        <hr>
        <div>
            <span><i class="pi pi-info-circle"></i>&nbsp;Claims belonging to other Verifiers
            </span>
        </div>
        <h5>Set to Pend Info Status</h5>
        <hr>
        <div formArrayName="claimArr">
            <p-table [value]="claimArr?.controls"
                [filterDelay]="0"
                [loading] = "loading"
                styleClass="p-datatable-gridlines p-datatable-striped"
                #dataTable
                [(selection)] = "selectedRows"
                [selectAll]="selectAll"
                (selectAllChange)="onSelectAllChange($event)"

                >
                <ng-template pTemplate="header" let-columns>
                    <tr>
                        <th scope="col" pSortableColumn = "caseId">Case ID<p-sortIcon field="caseId"></p-sortIcon>
                            <p-columnFilter type="text" field="caseId" display="menu" matchMode="contains"></p-columnFilter>
                        </th>
                        <th scope="col" pSortableColumn = "caseId">Verifier<p-sortIcon field="verifier"></p-sortIcon>
                            <p-columnFilter type="text" field="verifier" display="menu" matchMode="contains"></p-columnFilter>
                        </th>
                        <th scope="col" pSortableColumn = "trgStartDte">Training Period Start<p-sortIcon field="trgStartDte"></p-sortIcon>
                            <p-columnFilter type="text" field="trgStartDte" display="menu" matchMode="contains"></p-columnFilter>
                        </th>
                        <th scope="col" pSortableColumn = "trgEndDte">Training Period End<p-sortIcon field="trgEndDte"></p-sortIcon>
                            <p-columnFilter type="text" field="trgEndDte" display="menu" matchMode="contains"></p-columnFilter>
                        </th>
                        <th scope="col">Item</th>
                        <th scope="col">Current Status</th>
                        <th scope="col">
                            <form [formGroup] = "captionGroup">
                                <div class = "grid align-items-center">
                                    <p-dropdown class = "col-10" *ngIf="params.letterTypeCode === 'S14'" [options]="rejectDropDownList" id="apprBasis" appendTo="body"  optionLabel="name" optionValue="key"
                                    placeholder="Select" [autoDisplayFirst] = "true" [style]="{'max-width': '14em', 'width':'100%'}" formControlName = "itemStatus"></p-dropdown>
                                    <p-tableHeaderCheckbox class = "col"></p-tableHeaderCheckbox>
                                </div>
                            </form>

                        </th>

                    </tr>
                </ng-template>
                <ng-template pTemplate="body" let-data let-rowIndex = "rowIndex" let-columns="columns"
                let-rowgroup="rowgroup"  let-rowspan="rowspan"
                >
                    <tr [formGroupName]="rowIndex"  *ngFor = "let innerCtrl of  getInnerArrayFormController(this.claimArr, rowIndex, 'itemList'); let i = index">
                        <td *ngIf="i===0" [attr.rowspan]="getInnerArrayFormController(this.claimArr, rowIndex, 'itemList').length">
                            {{ data.value.display }}
                        </td>
                        <td *ngIf="i===0" [attr.rowspan]="getInnerArrayFormController(this.claimArr, rowIndex, 'itemList').length">{{ data.value.verifierInit }} </td>
                        <td *ngIf="i===0"[attr.rowspan]="getInnerArrayFormController(this.claimArr, rowIndex, 'itemList').length">{{ data.value.trgStartDte }} </td>
                        <td *ngIf="i===0"[attr.rowspan]="getInnerArrayFormController(this.claimArr, rowIndex, 'itemList').length">{{ data.value.trgEndDte }} </td>
                        <td >
                            {{innerCtrl.get('itemName').value | titlecase}}
                        </td>
                        <td >
                            {{innerCtrl.get('latestItemStatusDesc').value | titlecase}}
                        </td>
                        <td class="flex justify-content-center"><p-tableCheckbox [value]="innerCtrl.get('claimItemId').value"></p-tableCheckbox></td>

                    </tr>
                </ng-template>
            </p-table>
        </div>

    </div>
    <!-- [style]= "{'height': '100vh','position':'relative'}" -->
    <div class = "flex footer-button" >
        <div class = "flex col-6 justify-content-start flex-wrap">
            <button   pButton label="Cancel" (click) = "onCancel()">
            </button>

            <button   pButton label="TESTER" (click) = "tester()">
            </button>
        </div>
        <div class = "flex col-6 justify-content-end flex-wrap">
            <button   pButton label="Change Status" (click) = "openDialog($event, 'changeStatus')" class="p-button-success">
            </button>
        </div>
    </div>
    <p-confirmPopup key = "changeStatus" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>

</form>
