import { Component, OnInit } from '@angular/core';
import { FieldType, FieldTypeConfig } from '@ngx-formly/core';
import { InputNumberModule } from 'primeng/inputnumber';

@Component({
  selector: 'n2-formly-currency-type',
  template: `
    <input type="text" pInputText [formControl]="formControl" 
        [formlyAttributes]="field" [placeholder]="to.placeholder" maxlength="3" (keyup)="capValue()" (keydown)="allowKeys($event)">
  `,
})
export class FormlyCurrencyTypeComponent extends FieldType<FieldTypeConfig> implements OnInit {

  constructor() {
    super();
  }

  ngOnInit(): void {
    //
  }

  allowKeys(e: any) {
    return (
      e.keyCode === 8 || e.keyCode === 46 ||
      (e.keyCode >= 97 && e.keyCode <= 122) ||
      (e.keyCode >= 65 && e.keyCode <= 90) ||
      (e.keyCode >= 37 && e.keyCode <= 40)
    )
  }

  capValue() {
    let value: string = this.formControl.value;
    value = value.toUpperCase();
    this.formControl.patchValue(value);
  }
}
