import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { CodeType } from './code-type.model';
import { CodeTypeService } from './code-type.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { CodeTypeDeleteDialogComponent } from '../code-type/code-type-delete-dialog.component';

@Component({
  selector: 'ic-code-type',
  templateUrl: './code-type.component.html',
})
export class CodeTypeComponent implements OnInit, OnDestroy {
  currentAccount: any;
  codeTypes: CodeType[] = [];
  appList: any[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  currentSearch?: string;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;

  constructor(
    private codeTypeService: CodeTypeService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager,
    protected modalService: NgbModal
  ) {
    this.searchCriteria = {
      appId: '',
      codeType: '',
      codeTypeDesc: '',
    };
  }

  loadAll(): void {
    this.codeTypeService
      .query({
        appId: this.searchCriteria.appId,
        codeType: this.searchCriteria.codeType,
        codeTypeDesc: this.searchCriteria.codeTypeDesc,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['/admin/ic-codetype'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'id';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      appId: '',
      codeType: '',
      codeTypeDesc: '',
    };
    this.page = 0;
    this.router.navigate([
      '/admin/ic-codetype',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.handleNavigation();
    this.getAppList();
    this.registerChangeInCodeTypes();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  getAppList(): void {
    this.codeTypeService.getAppList().subscribe((data: HttpResponse<any>) => {
      this.appList = data.body;
    });
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'id') {
      result.push('id');
    }
    return result;
  }

  delete(codeType: CodeType): void {
    const modalRef = this.modalService.open(CodeTypeDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.codeType = codeType;
  }

  registerChangeInCodeTypes(): void {
    this.eventSubscriber = this.eventManager.subscribe('codeTypeListModification', () => this.loadAll());
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.codeTypes = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="codeAdmin.codeType.title">Code Types</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form id="codeTypeForm" class="col-sm-12">
      <div class="card">
        <div class="card-header">
          <span icTranslate="codeAdmin.codeType.search">Search Criteria</span>
        </div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label"><span icTranslate="codeAdmin.codeType.application">Application</span></label>
            <div class="col-sm-4">
              <select class="form-control" [(ngModel)]="searchCriteria.appId" id="appId" name="appId">
                <option [ngValue]=""></option>
                <option *ngFor="let app of appList" [value]="app.appId">{{ app.appName }}</option>
              </select>
            </div>

            <label class="col-sm-2 form-control-label"><span icTranslate="codeAdmin.codeType.main">Code Type</span></label>
            <div class="col-sm-4">
              <input type="text" class="form-control" [(ngModel)]="searchCriteria.codeType" id="codeType" name="codeType" />
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label"><span icTranslate="codeAdmin.codeType.desc">Description</span></label>
            <div class="col-sm-4">
              <input type="text" class="form-control" [(ngModel)]="searchCriteria.codeTypeDesc" id="codeTypeDesc" name="codeTypeDesc" />
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button id="search" type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>&nbsp;<span icTranslate="codeAdmin.btn.search">Search</span>
            </button>
            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>&nbsp;Reset
            </button>
            <button class="btn btn-primary btn-sm" [routerLink]="['/admin/ic-codetype/ic-codetype-new']">
              <fa-icon [icon]="'plus'"></fa-icon>
              <span icTranslate="codeAdmin.codeType.createLabel">Create new Code Type</span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />

  <div class="card">
    <div class="card-header">
      <span icTranslate="codeAdmin.codeType.searchResult">Search Result</span>
    </div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="codeTypes">
        <table class="table table-striped">
          <thead>
            <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
              <th scope="col"><span icTranslate="codeAdmin.codeType.sn">S/No.</span></th>
              <th scope="col" icSortBy="appId">
                <span icTranslate="codeAdmin.codeType.application">Application</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="codeTypeId">
                <span icTranslate="codeAdmin.codeType.main">Code Type</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="sCodeTypeDesc">
                <span icTranslate="codeAdmin.codeType.desc">Tel Main</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" style="text-align: center">
                <span icTranslate="acmAdmin.home.operation">Operation</span>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let codeType of codeTypes; index as i">
              <td>{{ itemsPerPage * (page - 1) + i + 1 }}</td>
              <td>{{ codeType.appId }}</td>
              <td>{{ codeType.codeTypeId }}</td>
              <td>{{ codeType.codeTypeDesc }}</td>
              <td class="text-center">
                <div class="btn-group flex-btn-group-container">
                  <button
                    type="submit"
                    [routerLink]="['/admin/ic-codetype/' + codeType.codeTypePk]"
                    [replaceUrl]="true"
                    class="btn btn-primary btn-sm"
                  >
                    <fa-icon [icon]="'pencil-alt'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.edit">Edit</span></button
                  >&nbsp;
                  <button
                    type="submit"
                    [routerLink]="['/admin/ic-code/', codeType.codeTypePk]"
                    [replaceUrl]="true"
                    class="btn btn-info btn-sm"
                  >
                    <fa-icon [icon]="'tasks'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="codeAdmin.code.title">Manage Code</span></button
                  >&nbsp;
                  <button type="submit" (click)="delete(codeType)" class="btn btn-danger btn-sm">
                    <fa-icon icon="times"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.delete">Delete</span>
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div *ngIf="codeTypes && codeTypes.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
