import { ValidatorFn, AbstractControl, FormControl } from '@angular/forms';
import { NSPAppConstants } from 'app/shared/constants/nspapp.constants';
import moment from 'moment';

export class RemarkValidator {

    static rejRemark(rem1: string): ValidatorFn {
        return (c: AbstractControl): { [key: string]: boolean } | null => {
            const amt = c.get(rem1).value;
            if (amt === null || amt.length > 2000) {
                return { decpoint: true };
            } else {
                return null;
            }
        };
    }


}
