import { Component, OnInit, ChangeDetectorRef } from '@angular/core';
import { HttpErrorResponse, HttpResponse, HttpStatusCode } from '@angular/common/http';
import { DynamicDialogRef } from 'primeng/dynamicdialog';
import { IAccountReviewer } from '../../../model/access-right-review.model';
import { AccessRightReviewService } from '../../../service/access-right-review.service';


@Component({
    selector: 'n2-accounts-reviewers-table',
    templateUrl: './accounts-reviewers-table.component.html',
    styleUrls: ['./accounts-reviewers-table.component.scss'],
})

export class AccountsReviewersTableComponent implements OnInit {

    accountReviwersList: IAccountReviewer[];
    loading?: boolean;

    constructor(
        private dialogRef: DynamicDialogRef,
        private accessRightReviewService: AccessRightReviewService,
        private changeDetectorRef: ChangeDetectorRef
    ) { }

    ngOnInit(): void {
        this.loading = false;
        this.changeDetectorRef.detectChanges();
        this.loadData();
    }

    loadData(): void {
        console.log("load")
        this.loading = true;
        this.changeDetectorRef.detectChanges();
        setTimeout(() => {
            this.accessRightReviewService.getAccountReviewers()
                .subscribe(
                    {
                        next: (res: HttpResponse<any>) => {
                            console.log(res.body?.data);
                            this.accountReviwersList = res.body.data;
                        },
                        error(error: HttpErrorResponse) {
                            console.log(error);
                        },
                        complete: () => {
                            console.log("load(): Completed")
                            this.loading = false;
                            this.changeDetectorRef.detectChanges();
                        }
                    }
                )
        }, 1000);
    }

    closeDialog(cond: boolean): void {
        this.dialogRef.close(cond);
    }

}
