import { Component, Input, OnInit, Output, EventEmitter } from '@angular/core';
import { EnqNSmen } from '../../model/nsman.model';

@Component({
  selector: 'n2-nsman-partial-nric-search',
  templateUrl: './nsman-partial-nric-search.component.html',
  styleUrls: ['./nsman-partial-nric-search.component.scss']
})
export class NsmanPartialNricSearchComponent implements OnInit {

  @Input() nsmen: EnqNSmen[];
  @Output() selectNSmanEvent = new EventEmitter<any>();

  constructor() {
    //
  }

  ngOnInit(): void {
    console.log("NSmen", this.nsmen);
  }

  handleSelection(nric: string): void {
    console.log("emit event to select nsman", nric);
    this.selectNSmanEvent.emit(nric);
  }

}
<p-table
    [value]="nsmen"
    styleClass="p-datatable-gridlines"
    [paginator]="true"
    [rows]="10"
    [rowsPerPageOptions]="[10,25,50]"
    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
    [showCurrentPageReport]="true"
    selectionMode="single">
    <ng-template pTemplate="caption">
        <span icTranslate="nspay2App.enquiry.nsman.partialNricSearch.enquiryResult"></span>
    </ng-template>
    <ng-template pTemplate="header">
        <tr>
            <th scope="col" style="width: 10%">SN</th>
            <th scope="col" style="width: 25%" pSortableColumn="nric">NRIC
                <p-sortIcon field="nric"></p-sortIcon>
                <p-columnFilter type="text" field="nric" display="menu" matchMode="contains"></p-columnFilter>
            </th>
            <th scope="col" style="width: 65%" pSortableColumn="name">Name
                <p-sortIcon field="name"></p-sortIcon>
                <p-columnFilter type="text" field="name" display="menu" matchMode="contains"></p-columnFilter>
            </th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-personnel let-i="rowIndex">
        <tr (click)="handleSelection(personnel.nric)">
            <td>{{ i + 1 }}</td>
            <td>{{ personnel.nric }}</td>
            <td>{{ personnel.name }}</td>
        </tr>
    </ng-template>
</p-table>
