import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import { SessionStorageService } from 'ngx-webstorage';
import { GlobalState } from '../sidebar/global.state';
import { VERSION } from 'app/app.constants';
import { LANGUAGES } from 'app/config/language.constants';
import { AccountService } from 'app/core/auth/account.service';
import { LoginService } from 'app/login/login.service';
import { ProfileService } from 'app/layouts/profiles/profile.service';

@Component({
  selector: 'ic-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.scss'],
})
export class NavbarComponent implements OnInit {
  inProduction?: boolean;
  isNavbarCollapsed = true;
  languages = LANGUAGES;
  openAPIEnabled?: boolean;
  version = '';
  public isMenuCollapsed = false;

  constructor(
    private loginService: LoginService,
    private translateService: TranslateService,
    private sessionStorage: SessionStorageService,
    private accountService: AccountService,
    private profileService: ProfileService,
    private _state: GlobalState,
    private router: Router
  ) {
    if (VERSION) {
      this.version = VERSION.toLowerCase().startsWith('v') ? VERSION : 'v' + VERSION;
      this._state.subscribe('menu.isCollapsed', (isCollapsed: boolean) => {
        this.isMenuCollapsed = isCollapsed;
      });
    }
  }

  ngOnInit(): void {
    this.profileService.getProfileInfo().subscribe(profileInfo => {
      this.inProduction = profileInfo.inProduction;
      this.openAPIEnabled = profileInfo.openAPIEnabled;
    });
  }

  changeLanguage(languageKey: string): void {
    this.sessionStorage.store('locale', languageKey);
    this.translateService.use(languageKey);
  }

  collapseNavbar(): void {
    this.isNavbarCollapsed = true;
  }

  isAuthenticated(): boolean {
    return this.accountService.isAuthenticated();
  }

  login(): void {
    this.router.navigate(['/login']);
  }

  logout(): void {
    this.collapseNavbar();
    this.loginService.logout();
    this.router.navigate(['']);
  }

  toggleNavbar(): void {
    this.isNavbarCollapsed = !this.isNavbarCollapsed;
  }

  public toggleMenu(): boolean {
    this.isMenuCollapsed = !this.isMenuCollapsed;
    this._state.notifyDataChanged('menu.isCollapsed', this.isMenuCollapsed);
    return false;
  }

  getImageUrl(): string {
    return this.isAuthenticated() ? this.accountService.getImageUrl() : '';
  }
}
  <nav class="navbar navbar-expand-md navbar-dark jh-navbar">
  <div class="jh-logo-container">
    <a href (click)="toggleMenu()" class="collapse-menu-link" *ngIf="isAuthenticated()">
      <fa-icon [icon]="'bars'" size="2x"></fa-icon>
    </a>
    <span class="n2-title-front" icTranslate="global.n2-title-front"></span>
    <span class="n2-title-back" icTranslate="global.n2-title-back"></span>
  </div>
  <ul class="navbar-nav ms-auto" style="display: block" id="ul-node" [ngSwitch]="isAuthenticated()">
    <li class="nav-item" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }">
      <a class="nav-link" routerLink="/" (click)="collapseNavbar()" id="home-menu">
        <span>
          <fa-icon [icon]="'home'"></fa-icon>
          <span class="hiddenNavbarSpan" icTranslate="global.menu.home">Home</span>
        </span>
      </a>
    </li>
    <li ngbDropdown class="nav-item dropdown pointer" *ngIf="languages">
      <a
        class="nav-link dropdown-toggle"
        ngbDropdownToggle
        href="javascript:void(0);"
        id="languagesnavBarDropdown"
        *ngIf="languages.length > 1"
      >
        <span>
          <fa-icon [icon]="'flag'"></fa-icon>
          <span class="hiddenNavbarSpan" icTranslate="global.menu.language">Language</span>
        </span>
      </a>
      <ul class="dropdown-menu" style="position: absolute" ngbDropdownMenu *ngIf="languages.length > 1">
        <li *ngFor="let language of languages">
          <a
            class="dropdown-item"
            [icActiveMenu]="language"
            href="javascript:void(0);"
            (click)="changeLanguage(language); collapseNavbar()"
            >{{ language | findLanguageFromKey }}</a
          >
        </li>
      </ul>
    </li>
    <li
      ngbDropdown
      class="nav-item dropdown pointer"
      placement="bottom-right"
      routerLinkActive="active"
      [routerLinkActiveOptions]="{ exact: true }"
    >
      <a class="nav-link dropdown-toggle" ngbDropdownToggle href="javascript:void(0);" id="account-menu">
        <span *ngIf="!getImageUrl()">
          <fa-icon [icon]="'user'"></fa-icon>
          <span class="hiddenNavbarSpan" icTranslate="global.menu.account.main"> Account </span>
        </span>
        <span *ngIf="getImageUrl()">
          <img [src]="getImageUrl()" class="profile-image img-circle" alt="Avatar" />
        </span>
      </a>
      <ul class="dropdown-menu dropdown-menu-right" style="position: absolute" ngbDropdownMenu>
        <li *ngSwitchCase="true">
          <a class="dropdown-item" routerLink="account/profile" routerLinkActive="active" (click)="collapseNavbar()">
            <fa-icon [icon]="'wrench'" [fixedWidth]="true"></fa-icon>
            <span icTranslate="profile.main">Profile</span>
          </a>
        </li>
        <li *ngSwitchCase="true">
          <a class="dropdown-item" (click)="logout()" id="logout">
            <fa-icon [icon]="'sign-out-alt'" [fixedWidth]="true"></fa-icon>
            <span icTranslate="global.menu.account.logout">Sign out</span>
          </a>
        </li>
        <li *ngSwitchCase="false">
          <a class="dropdown-item" (click)="login()" id="login">
            <fa-icon [icon]="'sign-in-alt'" [fixedWidth]="true"></fa-icon>
            <span icTranslate="global.menu.account.login">Sign in</span>
          </a>
        </li>
      </ul>
    </li>
  </ul>
</nav>
