import { Component, Input, OnInit } from '@angular/core';
import { NSPAppConstants } from 'app/shared/constants/nspapp.constants';

@Component({
  selector: 'n2-edit-remark',
  templateUrl: './edit-remark.component.html',
  styleUrls: ['./edit-remark.component.scss']
})
export class EditRemarkComponent implements OnInit {

  @Input() maxRemarksChar?: number = NSPAppConstants.MAXIMUM_REMARKS_CHARACTERS; // max num of chars
  remarksValue?: string;

  constructor() {
    //
  }

  ngOnInit(): void {
    //
  }

}
<h5>Remarks</h5>
<div class='flex'>
  <textarea [(ngModel)]='remarksValue' class='flex-auto' pInputTextarea [maxLength]='maxRemarksChar'></textarea>
</div>
<span>Characters left: {{maxRemarksChar-remarksValue?.length}} of {{maxRemarksChar}}</span>
