import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { IcCodeService } from 'app/ng-connect';

@Component({
  selector: 'n2-release-kah-rnsa-by-amount',
  templateUrl: './release-kah-rnsa-by-amount.component.html',
  styleUrls: ['./release-kah-rnsa-by-amount.component.scss']
})
export class ReleaseKahRnsaByAmountComponent implements OnInit {
  releaseKahRnsaByAmountForm: FormGroup = this.fb.group({
    amount: new FormControl(null, Validators.required),
    selectionMethod: new FormControl(null, Validators.required)
  });
  
  selectionMethodOptions: any;
  todayPayroll = 1000;

  constructor(
    private fb: FormBuilder,
    private codeService: IcCodeService
  ) {
  }

  ngOnInit(): void {
    this.loadSelectionMethod();
    this.releaseKahRnsaByAmountForm.get('selectionMethod').patchValue(this.selectionMethodOptions[0]);
  }

  loadSelectionMethod(): void {
    this.codeService.gets('kahRnsa_selection')
    .subscribe((codes: any[]) => {
      this.selectionMethodOptions = codes[0];
    });
  }

  isFormValid(): boolean {
    return this.releaseKahRnsaByAmountForm.valid;
  }

  log(): void {
    console.log("form", this.releaseKahRnsaByAmountForm.value);
  }

  reset(): void {
    this.releaseKahRnsaByAmountForm.reset();
    this.releaseKahRnsaByAmountForm.get('selectionMethod').patchValue(this.selectionMethodOptions[0]);
  }
}
<div class='grid align-items-center'>
  <div class='col-6'>Today's Payroll:</div>
  <div class='col-6 flex'>
    <p-inputNumber class='flex-auto' [(ngModel)]='todayPayroll' [disabled]="true"></p-inputNumber>
  </div>
</div>
<br>
<form [formGroup]='releaseKahRnsaByAmountForm'>
  <div class='grid align-items-center'>
    <div class='col-6'>Amount to Release:</div>
    <div class='col-6 flex'>
      <p-inputNumber class='flex-auto' formControlName='amount' id='amount'
                     currency='SGD' [max]='todayPayroll'></p-inputNumber>
    </div>
  </div>
  <div class='flex justify-content-end'>
    <span *ngIf="(releaseKahRnsaByAmountForm?.get('amount')?.hasError('required')
      && releaseKahRnsaByAmountForm?.get('amount')?.dirty)" class='p-error'>Required</span>
  </div>
  <br>

  <div class='grid align-items-center'>
    <div class='col-6'>Selection Method:</div>
    <div class='col-6'>
      <p-dropdown class='flex-auto' [options]='selectionMethodOptions'
                  appendTo="body" formControlName='selectionMethod' optionLabel="label.en-US" optionValue="codeId"
                  id='selectionMethod'></p-dropdown>
    </div>

  </div>
  <div class='flex justify-content-end'>
  <span *ngIf="(releaseKahRnsaByAmountForm?.get('selectionMethod')?.hasError('required')
      && releaseKahRnsaByAmountForm?.get('selectionMethod')?.dirty)" class='p-error'>Required</span>
  </div>
</form>
