import {AfterViewInit, Component, OnInit, ViewChild} from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormControl, FormGroup, Validators} from '@angular/forms';
import {ActivatedRoute, Router} from '@angular/router';
import { SEClaimService } from 'app/claim/se-claim/service/se-claim.service';
import { ClaimService } from 'app/claim/service/claim.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { DialogService } from 'primeng/dynamicdialog';
import { CodeView, IcCodeService } from 'app/ng-connect';

import {SaveSEClaimRequest} from "app/claim/se-claim/model/save-se-claim-request.model";

import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { HttpParams } from '@angular/common/http';
import { MenuItem } from 'primeng/api';
import { ClaimSectionComponent } from '../se-claim-verification/claim-section/claim-section.component';
import { WorkingDataSectionComponent } from '../se-claim-verification/working-data-section/working-data-section.component';
import { SerbApprovalSectionComponent } from '../se-claim-verification/serb-approval-section/serb-approval-section.component';
import { ItemDetailsSectionComponent } from '../se-claim-verification/item-details-section/item-details-section.component';
import { ActualTrainingDetailsSectionComponent } from '../se-claim-verification/actual-training-details-section/actual-training-details-section.component';

@Component({
  selector: 'n2-se-claim-certification',
  templateUrl: './se-claim-certification.component.html',
  styleUrls: ['./se-claim-certification.component.scss']
})
export class SeClaimCertificationComponent implements OnInit {

  seHardCopyDetails: any;
  caseId = "";
  params: any ={};
  nsManNric = "";
  nsManRank = "";
  nsManName = "";
  nsManPhoneNum = "";
  nsManGender = "";
  code?: CodeView;
  loading!: boolean;
  totalAmtPay: number;
  saveSEClaimRequest: SaveSEClaimRequest = {} as SaveSEClaimRequest;
  matchedAttd: any;
  irasDetails:any;
  claimType = "S"
  remarksDialogDisplay = new Map<string, boolean>();
  // displayCloseRejRemarks: any = {display: false};
  // displayRecompRemarks: any = {display: false};

  remarksForm: FormGroup = this.formBuilder.group({
    closedRejectedRemarks: new FormControl('', [Validators.required]),
    recompRemarks: new FormControl('', [Validators.required]),
  });

  buttonForm: FormGroup = this.formBuilder.group({
    nextButton: new FormControl('', [Validators.required]),
  });

  nextButtonOptions: MenuItem[] =[];

  saveClaimDto: any ={};
  trgStartDte: string;

  nsInfo3 = "";
  nsInfo4 = "";

  nextClaimDto: any = {}


  claimOptionShort = "";

  accordionStates: boolean[] = Array(8).fill(true);
  allExpanded = true;

  claimOptionShortList: any[]=[];
  @ViewChild('claimSection', {static: false})
  public claimSection?: ClaimSectionComponent;

  @ViewChild('serbApprovalSection', {static: false})
  public serbApprovalSection?: SerbApprovalSectionComponent;

  @ViewChild('workingDataSection', {static: false})
  public workingDataSection?: WorkingDataSectionComponent;

  @ViewChild('itemDetailsSection', {static: false})
  public itemDetailsSection?: ItemDetailsSectionComponent;

  @ViewChild('actualTrainingDetailsSection', {static: false})
  public actualTrainingDetailsSection?: ActualTrainingDetailsSectionComponent;

  constructor(
    private formBuilder: FormBuilder,
    public dateUtilsService: DateUtilsService,
    private seClaimService: SEClaimService,
    private toastService: CustomToastService,
    private claimService: ClaimService,
    private route: ActivatedRoute,
    private dialogService: DialogService,
    private codeService: IcCodeService,
    private router: Router,
    private nspConfirmService: NspConfirmationService,

  ) {
   }
  ngAfterViewInit(): void {
    const otherSEItemArr = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    console.log("other se item", otherSEItemArr);
    this.loading = false;
    console.log("all loaded")
  }

  ngOnInit(): void {
    this.loading = true;
    this.retrieveSEHardCopyClaimDetails();
    this.remarksDialogDisplay.set("closeRej", false);
    this.remarksDialogDisplay.set("recomp", false);

  }

  initalizeRequestChangesIndicator() {
    this.saveSEClaimRequest.claimId = this.params.claimId;
    this.saveSEClaimRequest.claimInboxId = this.params.claimInboxId;
    this.saveSEClaimRequest.claimSectionInd = false;
    this.saveSEClaimRequest.workingDataSectionInd = false;
    this.saveSEClaimRequest.itemDetailsSectionInd = false;
    this.saveSEClaimRequest.forSerbApprovalSectionInd = false;
    this.saveSEClaimRequest.serbRationaleSectionInd = false;
    this.saveSEClaimRequest.actualTrainingSectionInd = false;
  }

  retrieveSEHardCopyClaimDetails(){

    this.route.queryParams.subscribe((params) => {
      this.params = params;
      this.seClaimService.retrieveSEClaimDetails(this.params?.claimId, null).subscribe(resp => {
        if (resp.body.data) {
          this.seHardCopyDetails = resp.body.data;
          this.loadCodes('claim_option_short', this.seHardCopyDetails?.headerDto?.claimOption);
          this.initalizeRequestChangesIndicator();
          this.initializeNextButton();
        }
        // this.loading=false;
      });
    });
  }

  initializeNextButton(): void{
    console.log("initializing next button")
    let params = new HttpParams();
    params = params.append('claimInboxId',this.params?.claimInboxId);
    this.nextButtonOptions = [];
    this.claimService.nextClaim({params}).subscribe((resp:any)=> {
      if (resp && resp.ok) {
        if (resp.body.data) {
          this.nextClaimDto = resp.body.data;
          if(this.nextClaimDto?.nextRecomp){
            console.log("the next recomp claiminbox id", this.nextClaimDto.nextRecomp?.claimInboxId)
            this.nextButtonOptions.push(
              {
                label: 'Next Recomp', id: "1", command: () => {
                this.navigateVerificationPage(this.nextClaimDto.nextRecomp);
                }
              }
            )
          }
          if(this.nextClaimDto?.nextNric){
            console.log("the next nric claiminbox id", this.nextClaimDto.nextNric?.claimInboxId)
            this.nextButtonOptions.push(
              {
                label: 'Next Nric', id: "2", command: () => {
                this.navigateVerificationPage(this.nextClaimDto.nextNric);
                }
              }
            )
          }
          if(this.nextClaimDto?.nextDueDt){
            console.log("the next due date claiminbox id", this.nextClaimDto.nextDueDt?.claimInboxId)
            this.nextButtonOptions.push(
              {
                label: 'Next Due', id: "3", command: () => {
                this.navigateVerificationPage(this.nextClaimDto.nextDueDt);
                }
              }
            )
          }
          if(this.nextClaimDto?.nextOption){
            console.log("the next option claiminbox id", this.nextClaimDto.nextOption?.claimInboxId)
            this.nextButtonOptions.push(
              {
                label: 'Next Option', id: "4", command: () => {
                this.navigateVerificationPage(this.nextClaimDto.nextOption);
                }
              }
            )
          }

        }
      }
    })
    this.nextButtonOptions.sort((a: MenuItem,b: MenuItem)=> a.id.localeCompare(b.id));
  }

  navigateVerificationPage(dto: any): void{
    if(dto){
      this.router.navigate(['/claim/se-claim/claim-verification-se'], {queryParams: {
        claimId: dto.claimId,
        claimInboxId: dto.claimInboxId,
      }}).then(() => {
        window.location.reload();
      });;
    }
  }

  loadCodes(codeType: string, codeId: string): void {
    this.codeService.get(codeType, codeId)
    .subscribe((code: CodeView) => {
      this.claimOptionShort = code.label['en-US'] as string;
    });
  }

  saveClaim(closeRejectRemarks: string, recompRemarks: string): void{
    console.log("request",this.saveSEClaimRequest);
    if (this.saveSEClaimRequest.claimSectionInd) {
      this.claimSection.populateRequestWithClaimSectionChanges();
    }
    if (this.saveSEClaimRequest.workingDataSectionInd) {
      this.workingDataSection.populateRequestWithWDSectionChanges();
    }
    if (this.saveSEClaimRequest.itemDetailsSectionInd) {
      this.itemDetailsSection.populateRequestWithItemDetailsSectionChanges();
    }
    if (this.saveSEClaimRequest.forSerbApprovalSectionInd) {
      this.serbApprovalSection.populateRequestWithSerbApprovalSectionChanges();
    }
    if (this.saveSEClaimRequest.serbRationaleSectionInd) {
      console.log("test");
    }
    if (this.saveSEClaimRequest.actualTrainingSectionInd) {
      this.actualTrainingDetailsSection.populateRequestWithActTrgDetailsSectionChanges();
    }
    if(closeRejectRemarks){
      this.saveSEClaimRequest.closeRejectRemarks = closeRejectRemarks;
    }
    if(recompRemarks){
      this.saveSEClaimRequest.recompRemarks = recompRemarks;
    }
    this.seClaimService.saveSEClaimVerification(this.saveSEClaimRequest, null).subscribe((res) => {
      if (res) {
        console.log(res);
        // window.location.reload();
      }
    });
  }

  getClaimSectionDto(dto: any): void{
    this.saveClaimDto = dto;
  }

  getTrgStartDte(dto: any): void{
    this.trgStartDte = dto;
    console.log("trgstartdte is ", this.trgStartDte)

  }

  getTotalAmtPay(data: any): void{
    this.totalAmtPay = data;
    console.log("total amt", this.totalAmtPay)
  }

  getMatchAttd(data: any): void{
    this.matchedAttd = data;
    console.log("matchedAttd", this.matchedAttd)
  }

  getIrasDetails(data:any): void{
    this.irasDetails = data;
    console.log("iras details", this.irasDetails)
  }

  popUpLatestOwners(): void{
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
      claimId: this.seHardCopyDetails?.claimId
    }

    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-latest-owner'],{queryParams: params2}));
    console.log(url);
    window.open(url, "viewLatestOwner", 'location=0,menubar=0,status=0,height=1000, width=1500');
  }

  popUpPastClaim(): void{
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
      claimId: this.seHardCopyDetails?.claimId
    }

    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-past-claim'],{queryParams: params2}));
    console.log(url);
    window.open(url, "viewPastClaim", 'location=0,menubar=0,status=0,height=1000, width=1500');
  }

  popUpeClaim(): void{
    const params2 = {
      eClaimId: this.seHardCopyDetails?.eClaimId,
      claimId: this.seHardCopyDetails?.claimId
    }

    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-eclaim'],{queryParams: params2}));
    console.log(url);
    window.open(url, "vieweClaim", 'location=0,menubar=0,status=0');
  }

  popRemarks(): void{
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
    }

    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-remarks'],{queryParams: params2}));
    console.log(url);
    window.open(url, "viewRemarks", 'location=0,menubar=0,status=0,height=1000, width=1500');
  }

  popIras(): void{
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
      claimId: this.seHardCopyDetails?.claimId
    }

    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-iras'],{queryParams: params2}));
    console.log(url);
    window.open(url, "viewIras", 'location=0,menubar=0,status=0,height=1000, width=1500');
  }

  popSerbReport(): void{
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
      claimId: this.seHardCopyDetails?.claimId
    }
    console.log("params are the", params2)

    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-serb-report'],{queryParams: params2}));
    console.log(url);
    window.open(url, "viewSerbReport", 'location=0,menubar=0,status=0,height=1000, width=1500');
  }

  openPopup(event:any, type: string): void{
    this.nspConfirmService.onConfirmPopup(event, type, null,
      () => {

        switch (type) {
          case 'confirmNoActionReq':
            this.noActionReq();
            break;
        }
      }
    );
  }

  noActionReq(): void{
    console.log("no action required");
    let params = new HttpParams();
    params = params.append('claimId',this.seHardCopyDetails?.claimId);
    params = params.append('claimType',this.claimType);
    this.claimService.noActionReq({},{params}).subscribe(resp => {
      if (resp && resp.ok) {
        if (resp.body.data) {
          this.toastService.success(resp.body.data);
        }
      }
    },
    (error: any) => {
      console.log("error is" + String(error?.error?.error_desc));
      this.toastService.error(String(error?.error?.error_desc));
    });

  }

  checkAllExpanded():void{
    this.allExpanded = this.accordionStates.every(a => a === true);
  }

  expandCollapseAll(): void{
    if(this.allExpanded){
      this.accordionStates.fill(false,0,8);
    } else {
      this.accordionStates.fill(true,0,8);
    }
    this.checkAllExpanded();
  }

  openDialog(event: Event, type: string):void{
    console.log("btn is" + type);

    const recompStatus = ["PRA"];
    const closedTypeStatus = ["COR", "CNS", "CCO"];
    const currentItemLatestStatuses = this.itemDetailsSection.otherSEItemArr.controls.map((x: AbstractControl<any, any>)=>{
      return String(x.value.latestStatus);
    });
    console.log("current statuses", currentItemLatestStatuses);
    if(type === 'confirmSave' && !((closedTypeStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))) 
    && !(recompStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))){
      console.log("lola")
      this.nspConfirmService.onConfirmPopup(event, type, null,
        () => {
          console.log("called api");
          this.saveClaim(null, null);
        }
      );
    }else{
      if(type === 'confirmSave' && (closedTypeStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))){
        this.remarksDialogDisplay.set("closeRej", true);
      }
      if(type === 'confirmSave' && (recompStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))){
        this.remarksDialogDisplay.set("recomp", true);
      }
    }

    if(type === 'closeRej' || type === 'recomp'){
      this.nspConfirmService.onConfirmPopup(event, type, null,
          () => {
    
            switch (type) {
              case 'closeRej':
                if(this.remarksDialogDisplay.get("closeRej")){
                  this.remarksDialogDisplay.set("closeRej", false);
                }
                break;
              case 'recomp':
                if(this.remarksDialogDisplay.get("recomp")){
                  this.remarksDialogDisplay.set("recomp", false);
                }
                break;
            }
            let callApi = false;
            for(const [key,value] of this.remarksDialogDisplay){
              console.log(`Map key is:${key} and value is:${String(value)}`);
              if(value){
                callApi = true;
                break;
              }
            }
            if(!callApi){
              console.log("called api");
              this.saveClaim(this.remarksForm.get('closedRejectedRemarks').value, this.remarksForm.get('recompRemarks').value);
            }
          }
        );
    }
  }

  tester(): void{
    // console.log("the item form array", this.itemDetailsSection.otherSEItemArr);
    console.log("the menu", this.nextButtonOptions)

  }
}

<div class="sticky-header" style="background-color: white">
    <p-toolbar [styleClass]="claimOptionShort==='AVE'?'ave-toolbar': claimOptionShort==='ITX'? 'itx-toolbar': claimOptionShort==='REP'? 'rep-toolbar':'default-toolbar'">
        <div class="p-toolbar-group-left col-9">
            <div class="col-4" [style]="{'display':'flex', 'align-items':'center'}">
                <div class="row">
                    <h2>
                        {{ seHardCopyDetails?.headerDto?.caseId }} ({{ claimOptionShort }}) {{ seHardCopyDetails?.headerDto?.nsManNric }}
                    </h2>
                </div>
            </div>
            <div class="col-2">
                <div class="row">
                    <div class="col-auto">
                        <span>{{ seHardCopyDetails?.headerDto?.nsManRank }} {{ seHardCopyDetails?.headerDto?.nsManName }}</span>
                    </div>
                </div>
                <div class="row">
                    <div class="col-auto">
                        <span>
                            <span>{{ seHardCopyDetails?.headerDto?.nsManPhoneNum }} ({{ seHardCopyDetails?.headerDto?.nsManGender }})</span>
                        </span>
                    </div>
                </div>
            </div>
            <div class="col-6">
                <div class="row">
                    <div class="col-4">
                        <span>CAB: {{nsInfo3}}</span>
                    </div>
                    <div class="col-4">
                      {{seHardCopyDetails?.headerDto?.claimSource}}
                    </div>
                    <div class="col-4">
                        Reason: {{ params.reason }}
                    </div>
                </div>
                <div class="row">
                    <div class="col-auto">
                        <span>
                            Overlapping attendance: {{nsInfo4}} &nbsp; and 4 more attd
                        </span>
                    </div>
                </div>
            </div>
        </div>
        <div class="p-toolbar-group-right col-3">
            <div class ="col-12 flex align-items-center flex-wrap justify-content-end">
                <div class="flex align-items-center">
                    Due:
                </div>
                &nbsp;&nbsp;
                <div class="flex align-items-center">
                    <h2 [style]="{'text-align': 'center','margin':'0'}">23 JUL (MON)</h2>
                </div>
            </div>
        </div>
    </p-toolbar>
    <div class = "grid m-1">

        <!-- <div class = "col-6 nsp-align-left display-inline-block flex align-items-center" > -->
        <div class = "col-6 justify-content-start gap-4 flex align-items-center" >
          <div *ngIf ="seHardCopyDetails?.eClaimId"><a href='javascript:' (click)="popUpeClaim()">eClaim</a></div>
          <div><a href='javascript:' (click)="popUpPastClaim()">Past Claims</a></div>
          <!--TODO: when enquiry is ready with this screen-->
          <div><a href='#'>Enquiry CPF/IRAS</a></div>
          <div><a href='javascript:' (click)="popIras()">IRAS</a></div>
          <div><a href='javascript:' (click)="popUpLatestOwners()">Owners</a></div>
          <div><a href='javascript:' (click)="popSerbReport()">SERB Report</a></div>
          <div><a href='javascript:' (click)= "popRemarks()">Remarks</a></div>
          <div><a href='#'>Prepopulated</a></div>
        </div>
        <div class = "col-6 flex justify-content-end gap-2">

        <form [formGroup] = "buttonForm">
            <button pButton (click) = "expandCollapseAll()">{{this.allExpanded? "Collapse All": "Expand All"}}</button>
            <button pButton (click) = "openPopup($event,'confirmNoActionReq')" label="No action required"
            *ngIf = "seHardCopyDetails?.latestStatus === 'COM' && params.reason === 'Supp Doc'"></button>
            <button pButton label="Letter"></button>
            <ng-container *ngIf="nextButtonOptions.length<=0; else hasNextButton">
            </ng-container>
            <ng-template #hasNextButton>
                <ng-container *ngIf="nextButtonOptions.length>1; else singleNextButton">
                    <p-splitButton *ngIf="nextButtonOptions.length>1; else singleNextButton" [label]="nextButtonOptions[0].label"
                        (onClick)="nextButtonOptions[0].command()" [model]="nextButtonOptions"></p-splitButton>
                </ng-container>
            </ng-template>
            <ng-template #singleNextButton>
                <button pButton [label]="nextButtonOptions[0].label" (onClick)="nextButtonOptions[0].command()"></button>
            </ng-template>
            <!-- <button pButton label="Save" (click)="saveClaim()"></button> -->
            <button pButton label="Save" (click)="openDialog($event, 'confirmSave')"></button>
            <button pButton label="TESTER" (click)="tester()"></button>
        </form>
        </div>
    </div>
</div>

<p-accordion>
    <n2-claim-section #claimSection
                      [seHardCopyDetails]="seHardCopyDetails"
                      (saveClaimSectionEvent)="getClaimSectionDto($event)"
                      (trgStartDteEvent) ="getTrgStartDte($event)"
                      [saveSEClaimRequest] = "saveSEClaimRequest"
                      (click) = "checkAllExpanded()"
                      [(selected)] = "accordionStates[0]">
    </n2-claim-section>
    <n2-iras-dld-details-section [seHardCopyDetails]="seHardCopyDetails"
                                 (irasDetailsEvent)="getIrasDetails($event)"
                                 (click) = "checkAllExpanded()"
                                 [(selected)] = "accordionStates[1]">
    </n2-iras-dld-details-section>
    <n2-working-data-section #workingDataSection
                             [seHardCopyDetails]="seHardCopyDetails"
                             [itemDetailsSection]="itemDetailsSection"
                             (totalAmtPayEvent)="getTotalAmtPay($event)"
                             [saveSEClaimRequest] = "saveSEClaimRequest"
                             [trgStartDte] = "trgStartDte"
                             [irasDetails]="irasDetails"
                             (click) = "checkAllExpanded()"
                             [(selected)] = "accordionStates[2]">
    </n2-working-data-section>
    <n2-item-details-section #itemDetailsSection
                             [seHardCopyDetails]="seHardCopyDetails"
                             [totalAmtPay]="totalAmtPay"
                             (recomputeMatchedAttdEvent)="getMatchAttd($event)"
                             [saveSEClaimRequest] = "saveSEClaimRequest"
                             (click) = "checkAllExpanded()"
                             [(selected)] = "accordionStates[3]">
    </n2-item-details-section>
  <n2-serb-approval-section #serbApprovalSection
                            [saveSEClaimRequest] = "saveSEClaimRequest"
                            [seHardCopyDetails]="seHardCopyDetails"
                            (click) = "checkAllExpanded()"
                            [(selected)] = "accordionStates[4]">
  </n2-serb-approval-section>
  <n2-serb-rationale-amendment-section
                            [saveSEClaimRequest] = "saveSEClaimRequest"
                            [seHardCopyDetails]="seHardCopyDetails"
                            (click) = "checkAllExpanded()"
                            [(selected)] = "accordionStates[5]">
  </n2-serb-rationale-amendment-section>
    <n2-item-change-history-section  [seHardCopyDetails]="seHardCopyDetails"
                                     (click) = "checkAllExpanded()"
                                     [(selected)] = "accordionStates[6]">
    </n2-item-change-history-section>
    <n2-actual-training-details-section #actualTrainingDetailsSection
                                        [seHardCopyDetails]="seHardCopyDetails"
                                        [matchedAttd]="matchedAttd"
                                        [saveSEClaimRequest] = "saveSEClaimRequest"
                                        (click) = "checkAllExpanded()"
                                        [(selected)] = "accordionStates[7]">
</n2-actual-training-details-section>
</p-accordion>
<p-confirmPopup key = "confirmSave" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>
<p-confirmPopup key = "confirmNoActionReq" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>


<p-dialog header="Remarks" [visible]="remarksDialogDisplay.get('closeRej')"
[modal]="true" [style]="{width: '50vw'}" [draggable]="false" [resizable]="false" [formGroup]="remarksForm">
    <h6>Please key in remarks for submission of a claim with closed/rejected items</h6>
    <textarea rows="5"
    class="inputfield w-full"
    inputId = "remark"
    formControlName = "closedRejectedRemarks"
    maxlength="450"
    pInputTextarea
    ></textarea>
    <ng-template pTemplate="footer">
        <button pButton label="Cancel" type="button" (click)="remarksDialogDisplay.set('closeRej', false)" class="p-button-danger"></button>
        <button pButton label="Submit" type="button" (click)="openDialog($event, 'closeRej')"
        [disabled]="remarksForm.get('closedRejectedRemarks')?.hasError('required')"></button>
    </ng-template>
</p-dialog>
<p-confirmPopup key = "closeRej" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>

<p-dialog header="Remarks" [visible]="remarksDialogDisplay.get('recomp')"
[modal]="true" [style]="{width: '50vw'}" [draggable]="false" [resizable]="false" [formGroup]="remarksForm">
<h6>Please key in remarks for recomputation</h6>
<textarea rows="5"
    class="inputfield w-full"
    inputId = "remark"
    formControlName = "recompRemarks"
    maxlength="450"
    pInputTextarea
    ></textarea>
    <ng-template pTemplate="footer">
        <button pButton label="Cancel" type="button" (click)="remarksDialogDisplay.set('recomp', false)" class="p-button-danger"></button>
        <button pButton label="Submit" type="button" (click)="openDialog($event, 'recomp')"
        [disabled]="remarksForm.get('recompRemarks')?.hasError('required')"></button>
    </ng-template>
</p-dialog>
<p-confirmPopup key = "recomp" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>
