import {Component, Inject, LOCALE_ID, OnInit, ViewChild} from '@angular/core';
import {IBankFile} from 'app/payment/bankfile-stop-payment/bankfile/model/bank-file.model';
import {HttpErrorResponse, HttpResponse} from '@angular/common/http';
import {CustomToastService} from 'app/layouts/service/customtoast.service';
import {NspConfirmationService} from 'app/shared/confirm/nsp-confirm.service';
import {BankfileService} from 'app/payment/bankfile-stop-payment/bankfile/service/bankfile.service';
import {IPayrollDetail} from 'app/payment/bankfile-stop-payment/bankfile/model/payroll-details';
import {
  BankfileFileUploadComponent
} from 'app/payment/bankfile-stop-payment/bankfile/bankfile-file-upload/bankfile-file-upload.component';
import {PaymentService} from 'app/payment/overpayment/service/payment.service';
import {Ns2HttpService} from 'app/shared/service/ns2-http.service';
import {FilterMetadata, LazyLoadEvent} from 'primeng/api';
import {Ns2Status} from 'app/shared/constants/nspapp.constants';
import {Table} from 'primeng/table';
import {ExportUtilsService} from 'app/core/util/export-utils.service';
import {DateUtilsService} from 'app/core/util/date-utils.service';
import {formatDate} from '@angular/common';
import {FileUploadService} from 'app/shared/service/file-upload.service';
import {ScheduleService} from "app/admin/schedule/schedule.service";
import {Job} from "app/admin/schedule/schedule.model";

@Component({
  selector: 'n2-bankfile',
  templateUrl: './bankfile.component.html',
  styleUrls: ['./bankfile.component.scss']
})
export class BankfileComponent implements OnInit {
  displayBankfileFileUploadDialog: any = { display: false };

  bankfileArray: IBankFile[];
  selectedBankFile: IBankFile;
  job: Job = new Job();

  payrollDetail: IPayrollDetail;

  //Lazy Loading
  loading = false;
  totalRecords = 0;
  pageData: { currentPage: number } = {
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent: LazyLoadEvent;

  Ns2Status: { [key: string]: Ns2Status } = Ns2Status;

  isCurrentDtBlocked = false;

  hasCurrentDtRecord = false;
  checkedForCurrentDtRecord = false;

  @ViewChild('bankfileFileUpload', { static: true })
  private fileUploadComponent!: BankfileFileUploadComponent;

  constructor(
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private bankFileService: BankfileService,
    private scheduleService: ScheduleService,
    private paymentService: PaymentService,
    private ns2HttpService: Ns2HttpService,
    private dateUtilsService: DateUtilsService,
    private exportUtilsService: ExportUtilsService,
    private fileUploadService: FileUploadService,
    @Inject(LOCALE_ID) protected localeId: string
  ) {
  }

  ngOnInit(): void {
    this.loadPayrollDetail();
    this.scheduleService.findByNameAndGroup("M00045", "Daily").subscribe(res => {
      this.job = res.data;
      this.job.startDt = new Date(this.job.startDt as any);
      if (this.job.endDt) {
        this.job.endDt = new Date(this.job.endDt as any);
      }
      this.job.parameter = 'processDate=TODAY;';
    });
  }

  load(event: LazyLoadEvent): void {
    this.loading = true;
    this.lazyLoadEvent = event;

    const options = this.ns2HttpService.convertLazyEvent(event, this.pageData, this.pageSize);

    this.bankFileService.get(options).subscribe(
      (res: HttpResponse<any>) => {
        // Pipe response to an array of Interface model
        this.bankfileArray = res.body?.data.content;
        this.totalRecords = res.body?.data.totalElements;
        this.loading = false;
        this.checkForCurrentDt();
      },
      (error: HttpErrorResponse) => {
        console.log(error.error?.error_desc);
        this.loading = false;
      }
    );
  }

  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;
  }

  loadPayrollDetail(): void{
    this.paymentService.getPayrollDetail().subscribe(
      (res: HttpResponse<any>) => {
        this.payrollDetail = res.body?.data;
      },
      (error: HttpErrorResponse) => {
        console.log(error.error?.error_desc);
      }
    );
  }

  exportCSV(dataTable: Table): void {
    const options = this.ns2HttpService.convertLazyEvent(this.lazyLoadEvent, 0, this.totalRecords);

    this.bankFileService.get(options).subscribe(
      resp => {
        if(resp.body?.data?.content) {

          const data = resp.body?.data?.content;

          const columns =
          [
            {field: 'sn', header: 'SN', index: 0},
            {field: 'generatedDt', header: 'Generation Date', index: 1},
            {field: 'paydayDt', header: 'Pay Day', index: 2},
            {field: 'sentAmount', header: 'Sent Amount', index: 3},
            {field: 'downloadedBy', header: 'Downloaded By', index: 4},
            {field: 'seperator', header:' ', index:5},
            {field: 'uploadedBy', header: 'Uploaded By', index: 6},
            {field: 'successfulPayment', header: 'Successful Collection', index: 7},
            {field: 'unsuccessfulPayment', header: 'Unsuccessful Collection', index: 8},
            {field: 'status', header: 'Status', index: 9}
          ];

          const dtoList:any[] = [];
          console.log("data", data);
          data.forEach((d: IBankFile, index: number) => {
            const dto = {
              sn: index + 1,
              generatedDt: `${String(this.dateUtilsService.formatStrDate(d.generatedDt, "DD-MM-YYYY (HHmm)"))} ${String(d.generatedBy)}`,
              paydayDt: d.paydayDt,
              sentAmount: d.amountToBank,
              downloadedBy: `${String(this.dateUtilsService.formatStrDate(d.downloadedDt, "DD-MM-YYYY (HHmm)"))} ${String(d.downloadedBy ? d.downloadedBy : "")}`,
              seperator: " ",
              uploadedBy: `${String(this.dateUtilsService.formatStrDate(d.uploadedDt, "DD-MM-YYYY (HHmm)"))} ${String(d.uploadedBy ? d.uploadedBy : "")}`,
              successfulPayment: d.successPaymentAmt,
              unsuccessfulPayment: d.failedPaymentAmt,
              status: this.Ns2Status[d.status]
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList,resp.body.data.total_no_of_elems, columns,
            "data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }

  checkForCurrentDt(): void {
    if(!this.checkedForCurrentDtRecord) {
      const currentDt = new Date(new Date().toDateString());
      const found = this.bankfileArray.find((f: IBankFile) => currentDt.valueOf() === new Date(new Date(f.generatedDt).toDateString()).valueOf()) === undefined ? false : true;
      this.hasCurrentDtRecord = found;
      this.checkedForCurrentDtRecord = true;
    }
  }

  generatePaymentFile(): void {
    this.bankFileService.generate(this.job).subscribe(
      (res: HttpResponse<any>) => {
        this.toastService.success("Payment file will be generated. Please check back later");
        this.load(this.lazyLoadEvent);
      },
      (error: HttpErrorResponse) => {
        this.toastService.error(error.error?.error_desc);
      }
    );
  }

  downloadPayrollRun(payrollRunSummaryId: any): void {
    console.log('Downloading: ', payrollRunSummaryId);
    // TODO: Call the API to perform the download of Payroll Run
    // this.onSubmit('downloadPayrollRun', null);
  }

  downloadInitial(id: string): void {
    console.log('Triggered: ', id);

    this.bankFileService.download(id, {responseType: 'blob'}).subscribe(data => {
      if(data) {
        const contentDispositionHeader: string = data.headers.get('content-disposition');
        const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
        const blob = new Blob([data.body], { type: data.headers.get('content-type')});
        const anchor = document.createElement("a");
        anchor.href = URL.createObjectURL(blob);
        anchor.download = fileName;
        anchor.click();

        this.load(this.lazyLoadEvent);
      }
    }, error => {
      this.toastService.error(error.error?.error_desc);
    });
  }

  onOpen(dialog: any, bankFile: IBankFile): void {
    dialog.display = true;
    this.selectedBankFile = bankFile;
  }

  onUploadSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {

      // const fileId: any = {
      //   fileTxnId: this.fileUploadComponent.getVerifiedFiles().map(x=>
      //     String(x.meta_data.id)
      //   )[0]
      // }
      const fileTxnId: any = this.fileUploadComponent.getVerifiedFiles().map(x=>
          String(x.meta_data.id)
        )[0];

      this.bankFileService.upload(this.selectedBankFile.id, fileTxnId).subscribe(
        data => {
          this.toastService.success("Return file will be processed: Please check back later");
          this.load(this.lazyLoadEvent);
        },
        error => {
          this.toastService.error(error.error?.error_desc);
      });
    });
  }

  resetFile(): void {
    // this.fileUploadComponent.onRemoveAll();
  }

  downloadFromDMS(fileId: string): void {
    //TODO download via DMS
    if(fileId) {
      this.fileUploadService.downloadTxnId(fileId).subscribe(
        res => {
          if(res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type')});
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
            // TODO: Call API to update the BankFile record to update Downloaded By / DownloadedDt
          }
        },
        error => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }


  dateCallbackFilter(input: any, filter: (a: any) => void, id: string, table: Table): void {
    filter(new Date(input).toISOString());

    //Patch back
    const filterMetadata = table.filters[id] as FilterMetadata[];
    filterMetadata[0].value = input;
  }
}
<p-panel>
  <ng-template pTemplate='header'>
    <span class="p-panel-title">Payroll Run Details (As at {{ payrollDetail?.currentDt | date:'dd-MM-yyyy HHmm'  }})</span>
  </ng-template>
  <div class='grid'>
    <div class='col-8'>
      <div class='grid'>
        <div class='col-3 flex gap-2 align-items-center'>
          <div>
            <strong>Today's Payroll: <br>{{ payrollDetail?.currentPayroll | currency:'SGD' : ''| defaultString }}
            </strong>
          </div>
          <button pButton label='Download' (click)='downloadPayrollRun(1)'></button>
        </div>
        <div class='col-6 flex gap-4'>
          <div class='flex flex-column'>
            <div>
              <i class='pi pi-exclamation-triangle' style='color: gold'></i> Excluded (KAH/RNSA) - System:
            </div>
            <div>
              <i class='pi pi-exclamation-triangle' style='color: gold'></i> Excluded (All) - NPC:
            </div>
          </div>
          <div class='flex flex-column align-items-end'>
            <div>{{ payrollDetail?.kahRnsaExcludedCount}} record</div>
            <div>{{ payrollDetail?.allExcludedCount}} record</div>
          </div>
          <div class='flex flex-column align-items-end'>
            <div>{{ payrollDetail?.kahRnsaExcludedAmount | currency:'SGD' : ''}}</div>
            <div>{{ payrollDetail?.allExcludedAmount | currency:'SGD' : '' }}</div>
          </div>
        </div>
        <div class='col-3 flex gap-4'>
          <div>Protected Payable:</div>
          <div
            class='flex justify-content-end'>{{ payrollDetail?.protectedPayableCount}} record</div>
        </div>
      </div>
    </div>
    <div class='col-4 flex justify-content-end gap-4'>
      <button pButton label='Generate Payment File' (click)='generatePaymentFile()' [disabled]="hasCurrentDtRecord"></button>
      <button pButton label='Run Payroll Job' (click)='downloadPayrollRun(1)' [disabled]="hasCurrentDtRecord"></button>
    </div>
  </div>
</p-panel>

<br>

<p-table #bankFileDataTable [value]='bankfileArray' styleClass='p-datatable-gridlines' [paginator]='true' [rows]='5'
         [rowsPerPageOptions]='[10,25,50]'
         [showCurrentPageReport]='true'
         currentPageReportTemplate='Showing {first} to {last} of {totalRecords} entries'
         [lazy]='true'
        [totalRecords]='totalRecords'
        [loading]='loading'
        (onLazyLoad)='load($event)'
        (onPage) = "onPage($event)">
  <ng-template pTemplate='caption'>
    <div class='grid'>
      <div class='col-6 nsp-align-left flex justify-content-start align-items-center'>
        <button type='button' pButton pRipple icon='pi pi-file-o' (click)='exportCSV(bankFileDataTable)' pTooltip='Export as CSV'
                tooltipPosition='bottom'></button>
      </div>
    </div>
  </ng-template>
  <ng-template pTemplate='header'>
    <tr>
      <th scope="col" class='sn-header'>SN</th>
      <th scope="col" class='generation-date-header' pSortableColumn='generatedDt'>Generation Date<p-sortIcon field='generatedDt'></p-sortIcon>
        <p-columnFilter field="generatedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar dataType="string" [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'generatedDt', bankFileDataTable)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='payday-header'>Pay Day<p-sortIcon field="paydayDt"></p-sortIcon>
        <p-columnFilter type="date" field="sentAmount" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='sent-amount-header'>Sent Amount<p-sortIcon field="sentAmount"></p-sortIcon>
        <p-columnFilter type="numeric" field="sentAmount" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='downloaded-by-header'>Downloaded by<p-sortIcon field='downloadedDt'></p-sortIcon>
        <p-columnFilter field="downloadedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar dataType="string" [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'downloadedDt', bankFileDataTable)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='empty-divider-header'></th>
      <th scope="col" class='uploaded-by-header'>Uploaded by<p-sortIcon field='uploadedDt'></p-sortIcon>
        <p-columnFilter field="uploadedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar dataType="string" [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'uploadedDt', bankFileDataTable)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='successful-payment-header'>Successful Payment<p-sortIcon field='successfulPayment'></p-sortIcon>
        <p-columnFilter type="numeric" field="successfulPayment" display="menu" matchMode="contains"></p-columnFilter></th>
      <th scope="col" class='unsuccessful-payment-header'>Unsuccessful Payment<p-sortIcon field='unsuccessfulPayment'></p-sortIcon>
        <p-columnFilter type="numeric" field="unsuccessfulPayment" display="menu" matchMode="contains"></p-columnFilter></th>
      <th scope="col" class='status-header'>Status<p-sortIcon field='status'></p-sortIcon>
        <p-columnFilter type="text" field="status" display="menu" matchMode="contains"></p-columnFilter>
      </th>
    </tr>
  </ng-template>

  <ng-template pTemplate='body' let-bankFileRecord let-i='rowIndex'>
    <tr>
      <td>{{i + 1}}</td>
      <td>{{bankFileRecord?.generatedDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' | defaultString}}<br
        *ngIf='bankFileRecord?.generatedDt'>{{ bankFileRecord?.generatedBy}}</td>
      <td>
        <a href="javascript:void(0)" (click)="downloadFromDMS(bankFileRecord.fileToBankId)" *ngIf="bankFileRecord?.paydayDt && bankFileRecord?.status !== 'GENERATING'; else paydayDt">
          {{bankFileRecord?.paydayDt | date:'dd-MM-yyyy EE' | defaultString}}
        </a>
        <ng-template #paydayDt>
          {{bankFileRecord?.paydayDt | date:'dd-MM-yyyy EE' |  defaultString}}
        </ng-template>
      </td>
      <td>{{bankFileRecord?.amountToBank | currency:'SGD':'' | defaultString}}</td>
      <td
        *ngIf='bankFileRecord?.downloadedDt || bankFileRecord?.status !== "PENDING_DOWNLOAD"; else enableDownloadButton'>{{bankFileRecord?.downloadedDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' | defaultString}}
        <br
          *ngIf='bankFileRecord?.downloadedDt'>{{ bankFileRecord?.downloadedBy}}</td>
      <ng-template #enableDownloadButton>
        <td class='text-center'>
          <button pButton label='Download' (click)='downloadInitial(bankFileRecord?.id)'></button>
        </td>
      </ng-template>
      <td style='background-color: #f8f9fa'></td>
      <td
        *ngIf='bankFileRecord?.uploadedBy || bankFileRecord?.status !== "PENDING_RETURN"; else enableUploadButton'>
        <a href="javascript:void(0)" (click)="downloadFromDMS(bankFileRecord.fileFromBankId)"
          *ngIf="bankFileRecord?.fileFromBankId; else empty">
          {{bankFileRecord?.uploadedDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' | defaultString}}
          <br *ngIf='bankFileRecord?.uploadedBy'>{{ bankFileRecord?.uploadedBy}}
        </a>
      </td>
      <ng-template #enableUploadButton>
        <td class='text-center'>
          <button pButton label='Upload' (click)='onOpen(displayBankfileFileUploadDialog, bankFileRecord)'></button>
        </td>
      </ng-template>
      <td style='text-align: end'>{{bankFileRecord?.successPaymentAmt | currency:'SGD':'' | defaultString }}</td>
      <!--download of additional result file -->
      <td style='text-align: end'>
        <a href="javascript:void(0)" (click)="downloadFromDMS(bankFileRecord.failedResultFileId)"
          *ngIf="bankFileRecord?.failedResultFileId && bankFileRecord?.failedPaymentAmt > 0; else failedPaymentAmt">
          {{bankFileRecord?.failedPaymentAmt | currency:'SGD':'' | defaultString }}</a>
      </td>
      <ng-template #failedPaymentAmt>
        {{bankFileRecord?.failedPaymentAmt | currency:'SGD':'' | defaultString }}
      </ng-template>
      <td [ngStyle]="bankFileRecord.status?.toUpperCase().includes('ERROR') && {'color': 'red'}">{{ Ns2Status[bankFileRecord?.status] | defaultString }}</td>
    </tr>
  </ng-template>

  <ng-template pTemplate='emptymessage'>
    <tr>
      <td colspan='10'>No Bank File records found.</td>
    </tr>
  </ng-template>
</p-table>

<p-dialog header='Upload Bank Return File' [(visible)]='displayBankfileFileUploadDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true' (onHide)='resetFile()'>

  <n2-bankfile-file-upload #bankfileFileUpload></n2-bankfile-file-upload>

  <ng-template pTemplate='footer'>
    <div class="flex justify-content-end align-items-center gap-2">
      <div>
        <button pButton class='p-button-danger' label='Cancel' (click)='displayBankfileFileUploadDialog.display=false'></button>
      </div>
      <div>
        <p-confirmPopup key='bankfileFileUpload'></p-confirmPopup>
        <button pButton label='Submit' (click)="onUploadSubmit($event, 'bankfileFileUpload', displayBankfileFileUploadDialog)"></button>
      </div>
    </div>
  </ng-template>
</p-dialog>

<ng-template #empty>
  -
</ng-template>
