import { Injectable } from '@angular/core';
import { BehaviorSubject, Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CustomToastService {


  data$: Subject<any> = new BehaviorSubject<any>(null);

  get listen(): BehaviorSubject<any> {
    return (this.data$ as BehaviorSubject<any>);
  }

  add(input: any) : void{
    this.data$.next(input);
  }

  public success(msg:string): void{
    this.data$.next({
      severity:'success',
      summary:'Success',
      detail: msg
    })
  }

  public info(msg:string): void{
    this.data$.next({
      severity:'info',
      summary:'Info',
      detail: msg
    })
  }

  public warn(msg:string): void{
    this.data$.next({
      severity:'warn',
      summary:'Warn',
      detail: msg
    })
  }

  public error(msg:string): void{
    this.data$.next({
      severity:'error',
      summary:'Error',
      detail: msg
    })
  }
}
