      console.log("incoming value", value);
      if (_this.isActive) {
        if (value !== _this.partnerForm.get('captionGroup.radioOption').value) {
          e.preventDefault();
          const confirm = yield _this.showConfirm(e, type);
          if (!confirm) {
            return;
          } else {
            _this.partnerForm.get('captionGroup.radioOption').setValue(value);
            _this.partnerForm.get('consolidated').setValue(value === 'CON');
            _this.clearForm(value);
          }
        }
      }
    })();
  }
