  getNIPDiff() {
    let diff = 0;
    let annualNIP = 0;
    let consolNIP = 0;
    if (this.partnerForm?.get('captionGroup.radioOption').value === 'CON') {
      annualNIP = isFinite((this.consolDataFormArray.get('annual')?.get('netProfit').value || 0) / this.consolDataFormArray.get('annual')?.get('revenue').value * 100 || 0) ? (this.consolDataFormArray.get('annual')?.get('netProfit').value || 0) / this.consolDataFormArray.get('annual')?.get('revenue').value * 100 || 0 : 0;
      consolNIP = isFinite((this.dataFormArray.get('consolidated')?.get('netProfit').value || 0) / this.dataFormArray.get('consolidated')?.get('revenue').value * 100 || 0) ? (this.consolDataFormArray.get('consolidated')?.get('netProfit').value || 0) / this.consolDataFormArray.get('consolidated')?.get('revenue').value * 100 || 0 : 0;
    } else {
      annualNIP = isFinite((this.dataFormArray.get('annual')?.get('netProfit').value || 0) / this.dataFormArray.get('annual')?.get('revenue').value * 100 || 0) ? (this.dataFormArray.get('annual')?.get('netProfit').value || 0) / this.dataFormArray.get('annual')?.get('revenue').value * 100 || 0 : 0;
      consolNIP = isFinite(this.getSumOfCurrRow('netProfit') / this.getSumOfCurrRow('revenue') * 100) ? this.getSumOfCurrRow('netProfit') / this.getSumOfCurrRow('revenue') * 100 : 0;
    }
    this.consolNIP = this.roundVal(consolNIP);
    this.annualNIP = this.roundVal(annualNIP);
    diff = this.getAbsoluteDiff(this.annualNIP, this.consolNIP);
