  generateYrMth(dataSet) {
    //base range is 6 mths from backend, other wise is 6 mths from claim start mth - 6 to claim start mth -1 
    let claimStartDt;
    let claimEndDt;
    if (dataSet) {
      console.log("dataSet", dataSet);
      const claimStartYrMth = this.dateUtilsService.convertStrToDate(dataSet?.startYrmth, this.dateFormat);
      const claimEndYrMth = this.dateUtilsService.convertStrToDate(dataSet.endYrmth, this.dateFormat);
      claimStartDt = (0, moment_1.default)(new Date(claimStartYrMth.getFullYear(), claimStartYrMth.getMonth(), 1));
      claimEndDt = (0, moment_1.default)(new Date(claimEndYrMth.getFullYear(), +claimEndYrMth.getMonth() + 1, 0));
      const colStartDt = claimEndDt.clone().subtract(+this.partnerForm?.get('divisor').value - 1, 'months');
      const colEndDt = claimEndDt.clone();
      this.colMthRange = [];
      for (let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')) {
        this.colMthRange.push(mth.format("DD-MM-YYYY"));
      }
      this.yrmthList = this.colMthRange.map(x => (0, moment_1.default)(this.dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
      console.log("yr mth list", this.yrmthList);
    }
    //adds more form control if needed
    if (this.trgStartDte !== undefined && this.trgStartDte !== null) {
      console.log("incomign trg date", this.trgStartDte);
      claimStartDt = this.dateUtilsService.convertStrToMoment(this.trgStartDte, this.dateFormat).subtract(6, 'months');
      claimEndDt = this.dateUtilsService.convertStrToMoment(this.trgStartDte, this.dateFormat).subtract(1, 'months');
      const colStartDt = claimStartDt.clone();
      const colEndDt = claimEndDt.clone();
      this.colMthRange = [];
      for (let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')) {
        this.colMthRange.push(mth.format("DD-MM-YYYY"));
      }
      this.yrmthList = this.colMthRange.map(x => (0, moment_1.default)(this.dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
      console.log("yr mth list", this.yrmthList);
      this.adjustFormArray();
    }
    const cloneYrmthList = JSON.parse(JSON.stringify(this.yrmthList));
    console.log("clone is", cloneYrmthList);
    this.partnerForm.get('yrmthList').setValue(cloneYrmthList);
    this.partnerForm.get('startYrmth').setValue("01-".concat(cloneYrmthList[0]));
    this.partnerForm.get('endYrmth').setValue("01-".concat(cloneYrmthList[cloneYrmthList.length - 1]));
