  sortByAnnualandYrmthRecon() {
    return function (a, b) {
      if (a.type < b.type) {
        return -1;
      }
      if (a.type > b.type) {
        return 1;
      } else {
        //else 3
        return (0, moment_1.default)(a.yrmthReCon, "MM-YYYY") < (0, moment_1.default)(b.yrmthReCon, "MM-YYYY") ? -1 : 1;
      }
    };
  }
  constructInitialAnnualOptions(dataList, empty) {
    const annualData = dataList?.filter(x => x.type === '1')[0];
    if (!empty) {
      console.log("annual is ", annualData);
      if (annualData.year !== undefined && !this.partnerForm.get('yearInputOptions').value.find(y => y.name === annualData.year)) {
        const yearInputOptions = this.partnerForm.get('yearInputOptions').value;
        yearInputOptions.push({
          key: annualData.year,
          name: annualData.year
        });
        this.partnerForm.get('yearInputOptions').setValue(yearInputOptions);
      }
    }
  }
  constructForm(dataList, arrformGroup, empty, formBuilder) {
    this.constructInitialAnnualOptions(dataList, empty);
    if (arrformGroup === this.consolDataFormArray) {
      dataList.forEach(x => {
        //if annual type
        if (x.type === '1') {
          arrformGroup.addControl('annual', PartnerComponent.addRow(x, formBuilder));
          this.partnerForm.get('headerGroup.yearInput')?.setValue(x.year);
        } else if (x.type === '2') {
          //if consolidated type
          arrformGroup.addControl('consolidated', PartnerComponent.addRow(x, formBuilder));
        }
      });
      console.log("the consol form", arrformGroup);
    } else {
      dataList.forEach(x => {
        //if annual type
        if (x.type === '1') {
          console.log("the year is ", x);
          arrformGroup.addControl('annual', PartnerComponent.addRow(x, formBuilder));
          this.partnerForm.get('headerGroup.yearInput')?.setValue(x.year);
        } else if (x.type === '3') {
          //if mthly type
          arrformGroup.addControl(x?.yrmthReCon, PartnerComponent.addRow(x, formBuilder));
        }
      });
      console.log("the mthly form", arrformGroup);
    }
  }
