  exportAddMthly(yrmthFG, dataList, subWD, subsetYrmth, dtoList) {
    //mthly
    yrmthFG = this.partnerForm.get('dataFormArray').get('annual');
    subWD = {
      revenue: yrmthFG.get("revenue").value,
      netProfit: yrmthFG.get("netProfit").value,
      nsmanShareOfProfit: yrmthFG.get("nsmanShareOfProfit").value,
      nsmanPartnerSalary: yrmthFG.get("nsmanPartnerSalary").value,
      type: "1",
      year: this.partnerForm.get("headerGroup.yearInput").value
    };
    dataList.push(subWD);
    subsetYrmth.forEach(key => {
      yrmthFG = this.partnerForm.get('dataFormArray').get(key);
      subWD = {
        revenue: yrmthFG.get("revenue").value,
        netProfit: yrmthFG.get("netProfit").value,
        nsmanShareOfProfit: yrmthFG.get("nsmanShareOfProfit").value,
        nsmanPartnerSalary: yrmthFG.get("nsmanPartnerSalary").value,
        type: "3",
        yrmth: yrmthFG.get("yrmthReCon").value
      };
      dataList.push(subWD);
    });
    //add annual
    dataList.forEach(x => {
      if (x.type === "1") {
        dtoList[0]['annualAccount'] = x.revenue;
        dtoList[1]['annualAccount'] = x.netProfit;
        dtoList[2]['annualAccount'] = String(+x.netProfit / +x.revenue * 100).concat("%"); //Entity NIP
        dtoList[3]['annualAccount'] = x.nsmanShareOfProfit;
        dtoList[4]['annualAccount'] = String(+x.nsmanShareOfProfit / +x.netProfit * 100).concat("%"); //Nsman share %
        dtoList[5]['annualAccount'] = x.nsmanPartnerSalary;
        dtoList[6]['annualAccount'] = x.nsmanShareOfProfit + x.nsmanPartnerSalary; //total income
        dtoList[0]['aveAnnualAccount'] = x.revenue / this.partnerForm?.get('annualDivisor').value;
        dtoList[1]['aveAnnualAccount'] = x.netProfit / this.partnerForm?.get('annualDivisor').value;
        dtoList[2]['aveAnnualAccount'] = '';
        dtoList[3]['aveAnnualAccount'] = x.nsmanShareOfProfit / this.partnerForm?.get('annualDivisor').value;
        dtoList[4]['aveAnnualAccount'] = '';
        dtoList[5]['aveAnnualAccount'] = x.nsmanPartnerSalary / this.partnerForm?.get('annualDivisor').value;
        dtoList[6]['aveAnnualAccount'] = (x.nsmanShareOfProfit + x.nsmanPartnerSalary) / this.partnerForm?.get('annualDivisor').value;
      } else if (x.type === "3") {
        dtoList[0][x.yrmth] = x.revenue;
        dtoList[1][x.yrmth] = x.netProfit;
        dtoList[2][x.yrmth] = '';
        dtoList[3][x.yrmth] = x.nsmanShareOfProfit;
        dtoList[4][x.yrmth] = '';
        dtoList[5][x.yrmth] = x.nsmanPartnerSalary;
        dtoList[6][x.yrmth] = x.nsmanShareOfProfit + x.nsmanPartnerSalary;
      }
    });
    const mthlyDataList = dataList.filter(x => x.type === "3");
    dtoList[0]['mthAccount'] = this.getSumByField(mthlyDataList.map(x => {
      return x.revenue;
    }));
    dtoList[1]['mthAccount'] = this.getSumByField(mthlyDataList.map(x => {
      return x.netProfit;
    }));
    dtoList[2]['mthAccount'] = String(+dtoList[1]['mthAccount'] / +dtoList[0]['mthAccount'] * 100).concat("%");
    dtoList[3]['mthAccount'] = this.getSumByField(mthlyDataList.map(x => {
      return x.nsmanShareOfProfit;
    }));
    dtoList[4]['mthAccount'] = String(+dtoList[3]['mthAccount'] / +dtoList[1]['mthAccount'] * 100).concat("%");
    dtoList[5]['mthAccount'] = this.getSumByField(mthlyDataList.map(x => {
      return x.nsmanPartnerSalary;
    }));
    dtoList[6]['mthAccount'] = +dtoList[3]['mthAccount'] + +dtoList[5]['mthAccount'];
    dtoList[0]['aveMthAccount'] = dtoList[0]['mthAccount'] / this.partnerForm?.get('divisor').value;
    dtoList[1]['aveMthAccount'] = dtoList[1]['mthAccount'] / this.partnerForm?.get('divisor').value;
    dtoList[2]['aveMthAccount'] = "";
    dtoList[3]['aveMthAccount'] = dtoList[3]['mthAccount'] / this.partnerForm?.get('divisor').value;
    dtoList[4]['aveMthAccount'] = "";
    dtoList[5]['aveMthAccount'] = dtoList[5]['mthAccount'] / this.partnerForm?.get('divisor').value;
    dtoList[6]['aveMthAccount'] = dtoList[6]['mthAccount'] / this.partnerForm?.get('divisor').value;
  }
  getSumByField(fieldArray) {
    const initValue = 0;
    const sum = fieldArray.reduce((accumulator, currentValue) => accumulator + currentValue, initValue);
    return sum;
  }
