  static staticConstructConsolidatedHeaderData(dataList: any[], consolidated: boolean, empty: boolean, dateUtilsService: DateUtilsService, yrmthList: any[]): any[]{
    //initialize with 1 column annual income only
    let sortedData;
    const annualData = dataList.filter(x=> x.type==='1');
    if(annualData.length === 0){
      dataList.push({
        type: '1'
      })
    }

    let sanitizedDataList:any[] =[];

    if(consolidated || empty ){
      yrmthList.forEach((mth:any, colIndex:number)=>{
        dataList.push({
          type: '2'
        })
        dataList.push(
          {yrmthReCon: mth,
          type: '3'
          });
        });
      sortedData = dataList;
    }else{
      sanitizedDataList = dataList.filter(x=> (yrmthList.includes(x.yrmth) && x.type === '3') || x.type==='1');
      yrmthList.forEach(yrmth =>{
        const index = sanitizedDataList.findIndex(x=> x.yrmth ===  yrmth && x.type!=='1');
        if(index<0){
          sanitizedDataList.push({yrmthReCon: yrmth, type: '3'});
        }else{
          sanitizedDataList[index].yrmthReCon = yrmth;
        }
      })
      console.log("after data", sanitizedDataList);
      sortedData = sanitizedDataList;

    }
    return sortedData;

   }

