  constructConsolidatedHeaderData(dataList: any[], consolidated: boolean, empty: boolean): any[]{
    //initialize with 1 column annual income only
    let sortedData;
    const annualData = dataList.filter(x=> x.type==='1');
    if(annualData.length === 0){
      dataList.push({
        type: '1'
      })
    }

    let sanitizedDataList:any[] =[];

    if(consolidated || empty ){
      console.log("before before", dataList)
      this.colMthRange.forEach((mth:any, colIndex:number)=>{
        dataList.push({
          type: '2'
        })
        dataList.push(
          {yrmthReCon: moment(this.dateUtilsService.convertStrToDate(mth, "DD-MM-YYYY")).format("MM-YYYY"),
          type: '3'
          });
        });
      sortedData = dataList;
    }else{
      sanitizedDataList = dataList.filter(x=> (this.yrmthList.includes(x.yrmth) && x.type === '3') || x.type==='1');
      this.yrmthList.forEach(yrmth =>{
        const index = sanitizedDataList.findIndex(x=> x.yrmth ===  yrmth && x.type!=='1');
        if(index<0){
          sanitizedDataList.push({yrmthReCon: yrmth, type: '3'});
        }else{
          sanitizedDataList[index].yrmthReCon = yrmth;
        }
      })
      console.log("after data", sanitizedDataList);
      sortedData = sanitizedDataList.sort(this.sortByAnnualandYrmthRecon());

    }
    return sortedData;

   }

   constructDynamicMthRange(): void{
    this.colMthRange = [];
