  async confirmSwitch(e: Event,  type: string) {
    const value = (e.target as HTMLInputElement).value;
    console.log("incoming value",value)
    if(this.isActive){
      if (value !== this.partnerForm.get('captionGroup.radioOption').value) {
        e.preventDefault();
        const confirm = await this.showConfirm(e,type);      
        if (!confirm) {
          return;
        }else{
          this.partnerForm.get('captionGroup.radioOption').setValue(value);
          this.partnerForm.get('consolidated').setValue(value === 'CON')
          this.clearForm(value);
        }
      }
    }
   
  }

  showConfirm(e: Event, type: string) {
    return new Promise((resolve) => {
      this.confirmationService.confirm({
        target: e.target,
        message: 'All Working Data fields will be cleared',
        header: 'Confirmation',
        key: type,
        icon: 'pi pi-exclamation-triangle',
        acceptLabel: 'Proceed',
        rejectLabel: 'Cancel',
        accept() {
          return resolve(true)
        },
      });
    });
  }

