  getNetIncomeDiff(): number{
    let diff = 0;
    let annualTotalNI = 0;
    let consolTotalNI = 0;
    if(this.partnerForm?.get('captionGroup.radioOption').value==='CON'){
     
      annualTotalNI =  (+this.consolDataFormArray.get('annual')?.get('nsmanPartnerSalary').value + 
      +this.consolDataFormArray.get('annual')?.get('nsmanShareOfProfit').value )/this.partnerForm.get('annualDivisor').value;
      consolTotalNI =  (+this.consolDataFormArray.get('consolidated')?.get('nsmanPartnerSalary').value + 
      +this.consolDataFormArray.get('consolidated')?.get('nsmanShareOfProfit').value)/this.partnerForm.get('divisor').value;
    }else{
      annualTotalNI = (+this.dataFormArray.get('annual')?.get('nsmanPartnerSalary').value + 
      +this.dataFormArray.get('annual')?.get('nsmanShareOfProfit').value )/this.partnerForm.get('annualDivisor').value;
      consolTotalNI = (this.getSumOfCurrRow('nsmanPartnerSalary') + this.getSumOfCurrRow('nsmanShareOfProfit'))/ this.partnerForm.get('divisor').value;
