  retrievePartnerWD():void{  
    if((!this.currSelectedOption.changes && !this.currSelectedOption.newWD)) {
    let params = new HttpParams();
    params = params.append("workingDataId", this.currSelectedOption?.obj.workingDataId);

    this.partnerForm = this.formBuilder.group({
      id: new FormControl(null, null),
      workingDataId: new FormControl('',null),
      consolidated: new FormControl(false, null),
      divisor: new FormControl(6, null),
      annualDivisor: new FormControl(12, null),
      headerGroup: this.formBuilder.group({
        yearInput: new FormControl(0, null),
      }),
      dataFormArray: this.formBuilder.group({}),
      consolDataFormArray: this.formBuilder.group({}),
      captionGroup: this.formBuilder.group({
        radioOption: new FormControl(this.radioOptions[0].key, null)
      }),
      yrmthList:new FormControl([], null),
      startYrmth: new FormControl('', null),
      endYrmth: new FormControl('', null),
      yearInputOptions: new FormControl([], null)
    });
    this.seClaimService.retrieveSolePropPartnerWD({params}).subscribe((resp:any) => {
      // this.consolTable?.reset();
      // this.mthlyTable?.reset();
      if(resp){
          if(resp.status === 200 && resp.body.data){
            this.generateYrMth(resp.body.data)
            this.populatePartnerForm(resp.body.data, false);
            this.valueChangeSubscribe();

          }
        }
      },
      (error: any) => {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(String(error?.error?.error_desc));
      });
      
    }else if(this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      this.partnerForm = this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup as FormGroup;
      this.yrmthList = this.partnerForm.get('yrmthList').value;
      console.log("calling from exist", this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup)

      // this.generateYrMth(null);
      this.setRadioOption(this.partnerForm.get('consolidated').value);
      console.log("get from changes WD Map", this.partnerForm)
      this.loading = false;
      

  
    }else if(this.currSelectedOption.newWD) {
      console.log("selected key",this.currSelectedOption?.key.toString())
      this.partnerForm = this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup as FormGroup;
      console.log("get from new WD Map", this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup);

      if(this.partnerForm.get('consolidated')){
        this.setRadioOption(this.partnerForm.get('consolidated').value);
        //defaulted to monthly
      }else this.setRadioOption(false);

      this.loading = false;

    }
  }

  setRadioOption(consolidated: boolean): void{
    if(consolidated){
      this.partnerForm.get('captionGroup.radioOption').setValue('CON');
    }else this.partnerForm.get('captionGroup.radioOption').setValue('MTH');
  }


  populatePartnerForm(dataSet: any, newForm: boolean): FormGroup{
    this.dataSet = dataSet;
    const newDataList: any[] = [];
    let dataRecon:any[] = [];

    if(this.dataSet.consolidated === true){
      //expect 1
      if(this.dataSet.dataList){
        this.partnerForm?.get('captionGroup.radioOption').setValue('CON');
     
        dataRecon = this.constructConsolidatedHeaderData(this.dataSet.dataList, true, false);
        this.constructForm(dataRecon, this.consolDataFormArray, false, this.formBuilder);

        // construct empty for mthly view
        this.constructConsolidatedHeaderData(newDataList, false, true);
        this.constructForm(newDataList, this.dataFormArray, true, this.formBuilder);

 

      }
    }else{
      if(this.dataSet.dataList){
        this.partnerForm?.get('captionGroup.radioOption').setValue('MTH');
            //list of mthly records
        dataRecon = this.constructConsolidatedHeaderData(this.dataSet.dataList, false, false);
        this.constructForm(dataRecon, this.dataFormArray, false, this.formBuilder);

        //construct empty for consolidated view
        this.constructConsolidatedHeaderData(newDataList, true, true);
        this.constructForm(newDataList, this.consolDataFormArray, true, this.formBuilder);

      }
  
    }
    //set eligible mth divisor and prdn bonus divisor
    this.partnerForm.get('divisor').setValue(dataSet.divisor);
    this.partnerForm.get('annualDivisor').setValue(dataSet.annualDivisor);
    this.partnerForm.get('id').setValue(dataSet.id);
    this.partnerForm.get('workingDataId').setValue(dataSet.workingDataId);
    this.partnerForm.get('startYrmth').setValue(dataSet.startYrmth);
    this.partnerForm.get('endYrmth').setValue(dataSet.endYrmth);
    this.partnerForm.get('consolidated').setValue(this.dataSet.consolidated);
    this.loading = false;
    console.log("mika", this.partnerForm)
    return this.partnerForm;

  }

