  getSumOfCurrRow(formCtrlName: string): number
  {
    let sum = 0;
    let fieldCtrl;
    this.getYrmthList().slice(this.maxMths - this.partnerForm?.get('divisor').value, this.maxMths).forEach(yrmth => {//for each mth
      fieldCtrl = this.dataFormArray.get(yrmth).get(formCtrlName).value; // fieldCtrl is field level, i.e. revenue  
      sum+=fieldCtrl;
    });
    return sum;
  }

