  sortByAnnualandYrmthRecon(){
    return function (a: any, b: any) {
      if(a.type < b.type){
        return -1;
      }if(a.type > b.type){
        return 1;
      }else {
        //else 3
        return moment(a.yrmthReCon,"MM-YYYY") < moment(b.yrmthReCon,"MM-YYYY") ? -1 : 1;

      }
    }
  }

  constructInitialAnnualOptions(dataList: any[],  empty: boolean): void{
    const annualData = dataList?.filter(x=> x.type==='1')[0];
    if(!empty){
      console.log("annual is ", annualData);
      if(annualData.year!==undefined && !(this.partnerForm.get('yearInputOptions').value as any[]).find(y=>y.name === annualData.year)){
