  refreshOtherClaim(): void{
    let params = new HttpParams();
    params = params.append("nric", this.currHardCopyDetails?.headerDto.nsManNric);
    params = params.append("yrmthList", JSON.stringify(this.getYrmthList()));
    params = params.append("caseId", this.currHardCopyDetails?.headerDto.caseId);
    params = params.append("workingDataTypeCode", this.selectedWDOption?.obj.workingDataTypeCode);


    this.seClaimService.retrieveRefreshOtherSPPClaims({params}).subscribe((resp:any) => {
      if(resp){
        if(resp.status === 200 && resp.body.data){
          const refreshedArr:string[] = [];
          resp.body.data.forEach((y:any)=> {
            const ctrl = this.partnerForm.get('dataFormArray').get(y.yrmth)
            this.refreshOtherClaimSetValue(ctrl, y);
            refreshedArr.push(y.yrmth);
          });
          const existingYrMthControls =  Object.keys(this.partnerForm.get('dataFormArray'));
          const unrefreshed: string[] = refreshedArr.filter( x => !existingYrMthControls.includes(x));
          unrefreshed.forEach(unrefreshMths=> {
            this.refreshOtherClaimSetValue(this.partnerForm.get('dataFormArray').get(unrefreshMths), null);
          })
        }
      }
    });
  }

