      ctrl.get('revenue').setValue(x.revenue || null);
      ctrl.get('netProfit').setValue(x.netProfit ||null);
      ctrl.get('nsmanShareOfProfit').setValue(x.nsmanShareOfProfit ||null);
      ctrl.get('nsmanPartnerSalary').setValue(x.nsmanPartnerSalary || null);

    }
  }

