            <div style="margin-right:1em">
                ( Divisor:&nbsp;&nbsp;
                <p-inputNumber
                [min]="1" [max]="6" [inputStyle]="{'width': '5em', 'text-align':'right'}"
                formControlName = "divisor"
                [disabled] = "!isActive() || readOnly || !hasValidAuthorityAndStation()"></p-inputNumber>
                )
                <small *ngIf="partnerForm.get('divisor')?.touched && !isValidNumber(partnerForm.get('divisor')?.value)"
