                        <td>{{ cols[5] }}</td>
                        <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black'}"><div style="text-align:right">
                            {{ dataFormArray.get('annual').get('nsmanPartnerSalary').value +
                            dataFormArray.get('annual').get('nsmanShareOfProfit').value | number : '1.2-2'}}
                        </div></td>
                        <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'border-right': '2px solid black'}"><div style="text-align:right">
                            {{ (dataFormArray.get('annual').get('nsmanPartnerSalary').value +
                            dataFormArray.get('annual').get('nsmanShareOfProfit').value )/partnerForm.get('annualDivisor').value | number : '1.2-2'}}
                        </div></td>
                        <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black'}"><div style="text-align:right">
                            {{ getSumOfCurrRow('nsmanPartnerSalary') + getSumOfCurrRow('nsmanShareOfProfit')| number : '1.2-2'}}
                        </div>
                        <td  [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'border-right': '2px solid black', 'text-align':'right'}">
                            {{ (getSumOfCurrRow('nsmanPartnerSalary') + getSumOfCurrRow('nsmanShareOfProfit'))/partnerForm.get('divisor').value| number : '1.2-2'}}
                        </td>
                        <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'text-align':'right'}"
                        *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index" [formGroupName] = "yrmth">
                           {{ dataFormArray.get(yrmth).get('nsmanShareOfProfit').value +dataFormArray.get(yrmth).get('nsmanPartnerSalary').value | number : '1.2-2'}}
                        </td>
                    </tr>

                </ng-template>
            </p-table>
        </div>
        <div [formGroup]= "consolDataFormArray">
            <ng-template #consolOption>
                <p-table
                [value]="getYrmthList()"
                [filterDelay]="0"
                [loading] = "loading"
                styleClass="p-datatable-gridlines p-datatable-striped"
                responsiveLayout="scroll"
                #consolTable
                >
                    <ng-template pTemplate="header">
                        <tr>
                            <th scope="col"  [style]="{'width':'200px'}"></th>
                            <th scope="col" [attr.colspan]="partnerForm.controls['yrmthList'].value.length + 4">
                                <form [formGroup] = "captionGroup">
                                    <div class="grid">
                                        <div class = "col-6 nsp-align-left">
                                            <p-button icon="pi pi-file-o" (click) = "exportToCSV(consolTable)" [style]="{'margin-left': '.5em', 'margin-right': '.5em'}"
                                            ></p-button>
                                            <!--fieldset is needed to group the radio buyttons together for custom radio input buttons-->
                                            <fieldset *ngIf="isActive() && !readOnly && hasValidAuthorityAndStation(); else disabledRadio" [style]="{'display':'inline'}">
                                                <div [style]="{'display': 'inline','margin-left':'2em'}">
                                                    <input
                                                    id="custom-radio"
                                                    class = "p-radiobutton"
                                                    type="radio"
                                                    (click)="confirmSwitch($event, 'confirmWarning')"
                                                    value="MTH"
                                                    formControlName="radioOption"
                                                    />
                                                    <label class="custom-control-label">Monthly</label>
                                                </div>
                                                <div [style]="{'display': 'inline','margin-left':'2em'}">
                                                    <input
                                                    id="con-custom-radio"
                                                    class = "p-radiobutton"
                                                    type="radio"
                                                    (click)="confirmSwitch($event, 'confirmWarning')"
                                                    value="CON"
                                                    formControlName="radioOption"
                                                    />
                                                    <label class="custom-control-label">Consolidated</label>
                                                </div>
                                            </fieldset>
                                            <ng-template #disabledRadio>
                                                <fieldset disabled [style]="{'display':'inline'}">
                                                    <div [style]="{'display': 'inline','margin-left':'2em'}">
                                                        <input
                                                        id="custom-radio"
                                                        class = "p-radiobutton"
                                                        type="radio"
                                                        (click)="confirmSwitch($event, 'confirmWarning')"
                                                        value="MTH"
                                                        formControlName="radioOption"
                                                        />
                                                        <label class="custom-control-label">Monthly</label>

                                                    </div>
                                                    <div [style]="{'display': 'inline','margin-left':'2em'}">
                                                        <input
                                                        id="con-custom-radio"
                                                        class = "p-radiobutton"
                                                        type="radio"
                                                        (click)="confirmSwitch($event, 'confirmWarning')"
                                                        value="CON"
                                                        formControlName="radioOption"
                                                        />
                                                        <label class="custom-control-label">Consolidated</label>
                                                    </div>
                                                </fieldset>
                                            </ng-template>
                                        </div>
                                        <p-confirmPopup key = "confirmWarning" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>

                                        <div class = "col-6 nsp-align-right">
                                            <button *ngIf="isActive() && !readOnly && hasValidAuthorityAndStation()" id = "refreshOtherClaim" (click)="refreshOtherClaim()" pButton label="Refresh from Other Claim" [style]="{'margin-left': '.5em', 'margin-bottom': '.5em'}"
                                            [disabled] = !enableRefreshOClaim() >
                                            </button>
                                        </div>
                                    </div>
                                </form>
                            </th>
                        </tr>
                        <tr>
                            <th scope="col"></th>
                            <th scope="col" [style]="{'text-align': 'center'}">
                                <form [formGroup] = "headerGroup">

                                    <div class="col-5 input-group gap-2 flex align-content-center">
                                        <p-dropdown [options]="getFormControlValue(partnerForm, 'yearInputOptions')" id="source" [formControl]="getInnerFormControl(partnerForm, 'headerGroup', 'yearInput')" optionLabel="name" optionValue="key"
                                        [style]="{'width': '100%'}" autoWidth="false"  appendTo="body" [disabled]="!isActive || readOnly || !hasValidAuthorityAndStation()"></p-dropdown>
                                        <label class="col-form-label flex align-items-center">Account</label>
                                    </div>
                                </form>
                            </th>
                            <th scope="col" [style]="{'text-align': 'center', 'border-right': '2px solid black'}">Ave Monthly</th>
                            <th scope="col" [style]="{'text-align': 'center'}">6-Mth Account</th>
                            <th scope="col" [style]="{'text-align': 'center', 'border-right': '2px solid black'}">Ave Monthly</th>
                            <th scope="col" [style]="{'text-align': 'center'}" *ngFor="let yrmth of getYrmthList() | slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths">
                                {{yrmth}}
                            </th>
                        </tr>

                    </ng-template>
                    <ng-template pTemplate="body" let-columns = "cols" let-rowIndex = "rowIndex">
                        <tr  *ngIf="rowIndex== 0">
                            <td>{{ cols[0] }}</td>
                            <td formGroupName = "annual">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "revenue"
                                id="revenue" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'annual', 'revenue')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolAnnualRevenue"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolAnnualRevenue>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'annual', 'revenue').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                            </td>
                            <td [style]="{'border-right': '2px solid black'}"><div style="text-align:right">
                                {{ consolDataFormArray.get('annual').get('revenue').value/partnerForm.get('annualDivisor').value | number : '1.2-2'}}
                            </div></td>
                            <td formGroupName = "consolidated">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "revenue"
                                id="revenue" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'consolidated', 'revenue')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolConsolidatedRevenue"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolConsolidatedRevenue>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'consolidated', 'revenue').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                            </td>
                            <td [style]="{'border-right': '2px solid black', 'text-align':'right'}">
                                {{ consolDataFormArray.get('consolidated').get('revenue').value /partnerForm.get('divisor').value| number : '1.2-2'}}
                            </td>
                            <td *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index" >
                            </td>
                        </tr>
                        <tr  *ngIf="rowIndex== 1">
                            <td>{{ cols[1] }}</td>
                            <td formGroupName = "annual">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "netProfit"
                                id="netProfit" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'annual', 'netProfit')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolAnnualNetProfit"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolAnnualNetProfit>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'annual', 'netProfit').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                            </td>
                            <td [style]="{'border-right': '2px solid black'}"><div style="text-align:right">
                                {{ consolDataFormArray.get('annual').get('netProfit').value/partnerForm.get('annualDivisor').value | number : '1.2-2'}}
                            </div></td>
                            <td formGroupName = "consolidated">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "netProfit"
                                id="netProfit" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'consolidated', 'netProfit')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolConsolidatedNetProfit"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolConsolidatedNetProfit>
                                <div style="text-align:right">
                                    {{ getInnerFormControl(consolDataFormArray, 'consolidated', 'netProfit').value | number : '1.2-2'}}
                                </div>
                            </ng-template>
                            </td>
                            <td [style]="{'border-right': '2px solid black', 'text-align':'right'}">
                                {{ consolDataFormArray.get('consolidated').get('netProfit').value /partnerForm.get('divisor').value| number : '1.2-2'}}
                            </td>
                            <td *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index" >
                            </td>
                        </tr>
                        <tr  *ngIf="rowIndex== 2"  >
                            <td>{{ cols[2] }}</td>
                            <td>
                                <div style="text-align:right" *ngIf="consolDataFormArray.get('annual').get('revenue').value!==0 && consolDataFormArray.get('annual').get('revenue').value!==undefined && consolDataFormArray.get('annual').get('revenue').value!==null">
                                    {{ roundVal(consolDataFormArray.get('annual').get('netProfit').value/consolDataFormArray.get('annual').get('revenue').value * 100) | number : '1.0-0' }}%
                                </div>
                            </td>
                            <td [style]="{'border-right': '2px solid black'}"></td>
                            <td><div style="text-align:right" *ngIf="consolDataFormArray.get('consolidated').get('revenue').value!==0 && consolDataFormArray.get('consolidated').get('revenue').value!==undefined && consolDataFormArray.get('consolidated').get('revenue').value!==null">
                                {{ roundVal(consolDataFormArray.get('consolidated').get('netProfit').value/ consolDataFormArray.get('consolidated').get('revenue').value * 100) | number : '1.0-0' }}%
                            </div>
                            <td [style]="{'border-right': '2px solid black', 'text-align':'right'}"></td>
                            <td *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index" >
                            </td>
                        </tr>
                        <tr  *ngIf="rowIndex== 3">
                            <td>{{ cols[3] }}</td>
                            <td formGroupName = "annual">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "nsmanShareOfProfit"
                                id="nsmanShareOfProfit" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'annual', 'nsmanShareOfProfit')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolAnnualNsmanShareOfProfit"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolAnnualNsmanShareOfProfit>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'annual', 'nsmanShareOfProfit').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                               <div style="text-align:right; padding-top:1em" *ngIf="consolDataFormArray.get('annual').get('netProfit').value!==0 && consolDataFormArray.get('annual').get('netProfit').value!==undefined && consolDataFormArray.get('annual').get('netProfit').value!==null">
                                ({{ roundVal(consolDataFormArray.get('annual').get('nsmanShareOfProfit').value/consolDataFormArray.get('annual').get('netProfit').value * 100) | number : '1.0-0'  }}%)
                                </div>
                            </td>
                            <td [style]="{'border-right': '2px solid black'}"><div style="text-align:right">
                                {{ consolDataFormArray.get('annual').get('nsmanShareOfProfit').value/partnerForm.get('annualDivisor').value | number : '1.2-2'}}
                            </div></td>
                            <td formGroupName = "consolidated">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "nsmanShareOfProfit"
                                id="netProfit" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'consolidated', 'nsmanShareOfProfit')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolConsolidatedNsmanShareOfProfit"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolConsolidatedNsmanShareOfProfit>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'consolidated', 'nsmanShareOfProfit').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                               <div style="text-align:right; padding-top:1em" *ngIf="consolDataFormArray.get('consolidated').get('netProfit').value!==0 &&consolDataFormArray.get('consolidated').get('netProfit').value!==undefined && consolDataFormArray.get('consolidated').get('netProfit').value!==null">
                                ({{ roundVal(consolDataFormArray.get('consolidated').get('nsmanShareOfProfit').value/consolDataFormArray.get('consolidated').get('netProfit').value * 100) | number : '1.0-0'  }}%)
                            </div>
                            </td>
                            <td [style]="{'border-right': '2px solid black', 'text-align':'right'}">
                                {{ consolDataFormArray.get('consolidated').get('nsmanShareOfProfit').value /partnerForm.get('divisor').value| number : '1.2-2'}}
                            </td>
                            <td *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index" >
                            </td>
                        </tr>
                        <tr  *ngIf="rowIndex== 4"  >
                            <td>{{ cols[4] }}</td>
                            <td formGroupName = "annual">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "nsmanPartnerSalary"
                                id="nsmanPartnerSalary" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'annual', 'nsmanPartnerSalary')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolAnnualNsmanPartnerSalary"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolAnnualNsmanPartnerSalary>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'annual', 'nsmanPartnerSalary').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                            </td>
                            <td [style]="{'border-right': '2px solid black'}"><div style="text-align:right">
                                {{ consolDataFormArray.get('annual').get('nsmanPartnerSalary').value/partnerForm.get('annualDivisor').value | number : '1.2-2'}}
                            </div></td>
                            <td formGroupName = "consolidated">
                                <p-inputNumber  inputStyleClass = "nsp-align-right" formControlName = "nsmanPartnerSalary"
                                id="nsmanPartnerSalary" mode="decimal" [minFractionDigits]="2"
                                [formControl]="getInnerFormControl(consolDataFormArray, 'consolidated', 'nsmanPartnerSalary')"
                                *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation(); else labelConsolConsolidatedNsmanPartnerSalary"
                                >
                               </p-inputNumber>
                               <ng-template #labelConsolConsolidatedNsmanPartnerSalary>
                                    <div style="text-align:right">
                                        {{ getInnerFormControl(consolDataFormArray, 'consolidated', 'nsmanPartnerSalary').value | number : '1.2-2'}}
                                    </div>
                                </ng-template>
                            </td>
                            <td [style]="{'border-right': '2px solid black', 'text-align':'right'}">
                                {{ consolDataFormArray.get('consolidated').get('nsmanPartnerSalary').value/partnerForm.get('divisor').value| number : '1.2-2'}}
                            </td>
                            <td *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index" >
                            </td>
                        </tr>
                        <tr  *ngIf="rowIndex== 5"  >
                            <td>{{ cols[5] }}</td>
                            <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black'}"><div style="text-align:right">
                                {{ consolDataFormArray.get('annual').get('nsmanPartnerSalary').value +
                                consolDataFormArray.get('annual').get('nsmanShareOfProfit').value | number : '1.2-2'}}
                            </div></td>
                            <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'border-right': '2px solid black'}"><div style="text-align:right">
                                {{ (consolDataFormArray.get('annual').get('nsmanPartnerSalary').value +
                                consolDataFormArray.get('annual').get('nsmanShareOfProfit').value )/partnerForm.get('annualDivisor').value | number : '1.2-2'}}
                            </div></td>
                            <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black'}"><div style="text-align:right">
                                {{ consolDataFormArray.get('consolidated').get('nsmanPartnerSalary').value + consolDataFormArray.get('consolidated').get('nsmanShareOfProfit').value| number : '1.2-2'}}
                            </div>
                            <td  [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'border-right': '2px solid black', 'text-align':'right'}">
                                {{ (consolDataFormArray.get('consolidated').get('nsmanPartnerSalary').value + consolDataFormArray.get('consolidated').get('nsmanShareOfProfit').value)/partnerForm.get('divisor').value| number : '1.2-2'}}
                            </td>
                            <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'text-align':'right'}"
                            *ngFor="let yrmth of getYrmthList()| slice: ( maxMths - this.partnerForm?.get('divisor').value): maxMths; let i = index">

                            </td>
                        </tr>
                    </ng-template>
                </p-table>
            </ng-template>
        </div>
    </div>
    <div class="grid" style="margin-top:1em; margin-left: 220px">
        <div class = "col-12 justify-content-start gap-4 flex align-items-center" >
            <div>System Proposed Amount: {{ getSysProposedAmt() | number : '1.2-2'
            }}
            </div>
            <div [ngStyle]="{'color': getNIPDiff()>5? 'red': null}">NIP Difference: {{
                getNIPDiff()
            }} %
            ({{consolNIP}}% - {{annualNIP}}%)
        </div>
            <div [ngStyle]="{'color': getNetIncomeDiff()>1000? 'red': null}" >Net Income Difference: {{ getNetIncomeDiff() | number : '1.2-2' }} ({{consolNIAvg | number : '1.2-2'}} - {{annualNIAvg | number : '1.2-2'}})</div>
        </div>
        <div class = "col"  *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation()" >
            <p-button label="Copy to Item" (click)="copyToItem($event)" [disabled] = "!enableCopytoItem()"
            ></p-button>
            <p-button label="tester" (click)="testing()"
            ></p-button>
        </div>
    </div>

</form>
