import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { Subject } from '../user/user.model';
import { RoleService } from './role.service';
import { RoleAssignPopupService } from 'app/admin/access-control';

@Component({
  selector: 'ic-role-assign-users-dialog',
  templateUrl: './role-assign-users-dialog.component.html',
})
export class RoleAssignUsersDialogComponent implements OnInit {
  isSaving?: boolean;

  resourceId?: string;
  searchedUsers: Subject[] = [];
  chooseedUsers: Subject[] = [];
  previousUrl?: string;

  constructor(
    public activeModal: NgbActiveModal,
    private alertService: AlertService,
    private roleService: RoleService,
    private eventManager: EventManager,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.isSaving = false;
  }

  clear(): void {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  assign(): void {
    this.isSaving = true;
    if (this.chooseedUsers.length > 0) {
      this.roleService.assignUsers(this.resourceId, this.chooseedUsers).subscribe(() => {
        this.activeModal.dismiss('cancel');
        this.eventManager.broadcast({ name: 'assignedUserListModification', content: 'OK' });
        this.backToPrevious();
      });
    }
  }

  searchUnAssignUsers(event: any): void {
    this.roleService
      .searchUnAssignedUsers({
        resourceId: this.resourceId,
        firstName: event.query,
        page: 0,
        size: 1000,
        sort: null,
      })
      .subscribe(
        (res: HttpResponse<any>) => {
          this.searchedUsers = res.body;
        },
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }

  private backToPrevious(): void {
    setTimeout(() => {
      this.router.navigate(['admin/acm/ic-role/' + this.resourceId?.toString()]);
    }, 0);
  }
}

@Component({
  selector: 'ic-role-assign-users-popup',
  template: '',
})
export class RoleAssignUsersPopupComponent implements OnInit, OnDestroy {
  routeSub: any;

  constructor(private route: ActivatedRoute, private roleAssignPopupService: RoleAssignPopupService) {}

  ngOnInit(): void {
    this.routeSub = this.route.params.subscribe(params => {
      if (params['id']) {
        this.roleAssignPopupService.open(RoleAssignUsersDialogComponent as Component, params['id']);
      }
    });
  }

  ngOnDestroy(): void {
    this.routeSub.unsubscribe();
  }
}
<div class="modal-header">
  <h4 class="modal-title" id="myGroupLabel">Assign Users to Role</h4>
  <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
</div>

<div class="modal-body">
  <div class="rounded-pill row">
    <label class="col-sm-3 form-control-label">Search By First Name</label>
    <div class="col-sm-9">
      <p-autoComplete
        class="ui-fluid"
        [(ngModel)]="chooseedUsers"
        name="chooseedUsers"
        [suggestions]="searchedUsers"
        (completeMethod)="searchUnAssignUsers($event)"
        field="fullName"
        [multiple]="true"
      ></p-autoComplete>
    </div>
  </div>
</div>

<div class="modal-footer">
  <button
    type="button"
    [disabled]="isSaving || !chooseedUsers || chooseedUsers.length === 0"
    class="btn btn-primary btn-sm"
    (click)="assign()"
  >
    <fa-icon [icon]="'save'"></fa-icon>&nbsp;<span>Assign Users</span>
  </button>
  <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="clear()">
    <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
  </button>
</div>
