import { ValidatorFn, AbstractControl } from '@angular/forms';
import { validateNRIC } from '../../ng-connect/nric/nric-validate';

/* Validate NRIC for reactive form*/
export function NRICValidator(): ValidatorFn {

  return (c: AbstractControl) : { [key: string]: boolean } | null => {
    let isValid = false;
    
    if (c.value) {
        isValid = validateNRIC(c.value);
        if (isValid) {
          return {};
        } else {
          return { nric: true };
        }
      } else {
        return {};
      }
  }
}
