import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { UserToken } from 'app/admin/access-control/login-control/user-token.model';
import { UserTokenPopupService } from 'app/admin/access-control/login-control/user-token-popup.service';
import { UserTokenService } from 'app/admin/access-control/login-control/user-token.service';

@Component({
  selector: 'ic-user-token-dialog',
  templateUrl: './user-token-dialog.component.html',
})
export class UserTokenDialogComponent {
  userToken: UserToken = new UserToken();

  constructor(
    public activeModal: NgbActiveModal,
    private userTokenService: UserTokenService,
    private eventManager: EventManager,
    private router: Router
  ) {}

  clear(): void {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  confirmDelete(id: any): void {
    this.userTokenService.delete(id).subscribe(() => {
      this.eventManager.broadcast({
        name: 'userTokenListModification',
        content: 'Logout an User',
      });
    });
    this.activeModal.dismiss(true);
    this.backToPrevious();
  }

  backToPrevious(): void {
    setTimeout(() => {
      this.router.navigate(['admin/acm/ic-user-token']);
    }, 0);
  }
}

@Component({
  selector: 'ic-user-token-dialog-popup',
  template: '',
})
export class UserTokenPopupComponent implements OnInit, OnDestroy {
  routeSub: any;
  constructor(private route: ActivatedRoute, private userTokenPopupService: UserTokenPopupService) {}

  ngOnInit(): void {
    this.routeSub = this.route.params.subscribe(params => {
      if (params['id']) {
        this.userTokenPopupService.open(UserTokenDialogComponent as Component, params['id']);
      } else {
        this.userTokenPopupService.open(UserTokenDialogComponent as Component);
      }
    });
  }

  ngOnDestroy(): void {
    this.routeSub.unsubscribe();
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(userToken.id)">
  <div class="modal-header">
    <h4 class="modal-title"><span icTranslate="acmAdmin.userToken.revoke">Revoke Token</span></h4>
    <button type="button" class="close" data-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
  </div>
  <div class="modal-body">
    <jhi-alert-error></jhi-alert-error>
    <p icTranslate="acmAdmin.userToken.delete.question" [translateValues]="{ id: userToken.loginId }">
      Are you sure you want to revoke user token?
    </p>
  </div>
  <div class="modal-footer">
    <button type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="acmAdmin.userToken.revoke">Revoke</span>
    </button>
    <button type="button" class="btn btn-secondary btn-sm" data-dismiss="modal" (click)="clear()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
