    this.claimJson = claim;
  

   }

  //if use  this, will show null at the start since only called once
  ngAfterViewInit(): void {
    const otherSEItemArr = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    console.log("other se item", otherSEItemArr);
    this.loading = false;
  }

 

  ngOnInit(): void {
    this.loading = true;
    this.retrieveSEHardCopyClaimDetails();
    this.remarksDialogDisplay.set("closeRej", false);
    this.remarksDialogDisplay.set("recomp", false);

  }

  isValidAllForms(): boolean{
    return this.claimSection?.claimForm?.valid
    && this.actualTrainingDetailsSection.actualTrainingForm.valid;
  }

  initalizeRequestChangesIndicator() {
    this.saveSEClaimRequest.claimId = this.params.claimId;
    this.saveSEClaimRequest.claimInboxId = this.params.claimInboxId;
    this.saveSEClaimRequest.claimSectionInd = false;
    this.saveSEClaimRequest.workingDataSectionInd = false;
    this.saveSEClaimRequest.itemDetailsSectionInd = false;
    this.saveSEClaimRequest.forSerbApprovalSectionInd = false;
    this.saveSEClaimRequest.serbRationaleSectionInd = false;
    this.saveSEClaimRequest.actualTrainingSectionInd = false;
  }

  retrieveSEHardCopyClaimDetails(){

    this.route.queryParams.subscribe((params) => {
      this.params = params;
      this.readOnly = params.readOnly?.toLowerCase() === 'true';
      this.station = params.station?.toUpperCase();
      this.validateRoleAndStage();
      this.seClaimService.retrieveSEClaimDetails(this.params?.claimId, null).subscribe(resp => {
        if (resp.body.data) {
          this.seHardCopyDetails = resp.body.data;
          this.loadCodes('claim_option_short', this.seHardCopyDetails?.headerDto?.claimOption);
          this.initalizeRequestChangesIndicator();
          this.initializeNextButton();
          this.retrieveConflictApprovedSEClaim();
        }
        // this.loading=false;
      });
    });
  }



  initializeNextButton(): void{
    if(!this.readOnly){
      console.log("initializing next button")
      let params = new HttpParams();
      params = params.append('claimInboxId',this.params?.claimInboxId);
      this.nextButtonOptions = [];
      this.claimService.nextClaim({params}).subscribe((resp:any)=> {
        if (resp && resp.ok) {
          if (resp.body.data) {
            this.nextClaimDto = resp.body.data;
            if(this.nextClaimDto?.nextRecomp){
              console.log("the next recomp claiminbox id", this.nextClaimDto.nextRecomp?.claimInboxId)
              this.nextButtonOptions.push(
                {
                  label: 'Next Recomp', id: "1", command: () => {
                  this.navigateVerificationPage(this.nextClaimDto.nextRecomp);
                  }
                }
              )
            }
            if(this.nextClaimDto?.nextNric){
              console.log("the next nric claiminbox id", this.nextClaimDto.nextNric?.claimInboxId)
              this.nextButtonOptions.push(
                {
                  label: 'Next Nric', id: "2", command: () => {
                  this.navigateVerificationPage(this.nextClaimDto.nextNric);
                  }
                }
              )
            }
            if(this.nextClaimDto?.nextDueDt){
              console.log("the next due date claiminbox id", this.nextClaimDto.nextDueDt?.claimInboxId)
              this.nextButtonOptions.push(
                {
                  label: 'Next Due', id: "3", command: () => {
                  this.navigateVerificationPage(this.nextClaimDto.nextDueDt);
                  }
                }
              )
            }
            if(this.nextClaimDto?.nextOption){
              console.log("the next option claiminbox id", this.nextClaimDto.nextOption?.claimInboxId)
              this.nextButtonOptions.push(
                {
                  label: 'Next Option', id: "4", command: () => {
                  this.navigateVerificationPage(this.nextClaimDto.nextOption);
                  }
                }
              )
            }
  
          }
        }
      })
      this.nextButtonOptions.sort((a: MenuItem,b: MenuItem)=> a.id.localeCompare(b.id));
      console.log("the upser", this.nextButtonOptions)
    }
  }

  filteredNextButtonOptions(): MenuItem[]{
    if(this.nextButtonOptions.length>0){
      return this.nextButtonOptions.filter((x, index)=> index!==0);
    }else return null;
  }

  navigateVerificationPage(dto: any): void{
    if(dto){
      this.router.navigate(['/claim/se-claim/claim-verification-se'], {queryParams: {
        claimId: dto.claimId,
        claimInboxId: dto.claimInboxId,
      }}).then(() => {
        window.location.reload();
      });;
    }
  }

  loadCodes(codeType: string, codeId: string): void {
    this.codeService.get(codeType, codeId)
    .subscribe((code: CodeView) => {
      this.claimOptionShort = code.label['en-US'] as string;
    });
  }

  saveClaim(closeRejectRemarks: string, recompRemarks: string): void{
    console.log("request",this.saveSEClaimRequest);
    if (this.saveSEClaimRequest.claimSectionInd) {
      this.claimSection.populateRequestWithClaimSectionChanges();
    }
    if (this.saveSEClaimRequest.workingDataSectionInd) {
      this.workingDataSection.populateRequestWithWDSectionChanges();
    }
    if (this.saveSEClaimRequest.itemDetailsSectionInd) {
      this.itemDetailsSection.populateRequestWithItemDetailsSectionChanges();
    }
    if (this.saveSEClaimRequest.forSerbApprovalSectionInd) {
      this.serbApprovalSection.populateRequestWithSerbApprovalSectionChanges();
    }
    if (this.saveSEClaimRequest.serbRationaleSectionInd) {
      this.serbRationaleAmendmentSection.populateRequestWithSerbRationaleAmendmentSectionChanges();
    }
    if (this.saveSEClaimRequest.actualTrainingSectionInd) {
      this.actualTrainingDetailsSection.populateRequestWithActTrgDetailsSectionChanges();
    }
    if(closeRejectRemarks){
      this.saveSEClaimRequest.closeRejectRemarks = closeRejectRemarks;
    }
    if(recompRemarks){
      this.saveSEClaimRequest.recompRemarks = recompRemarks;
    }
    this.seClaimService.saveSEClaimVerification(this.saveSEClaimRequest, null).subscribe((res) => {
      if (res) {
        console.log(res);
        // window.location.reload();
      }
    });
  }

  getClaimSectionDto(dto: any): void{
    this.saveClaimDto = dto;
  }

  getTrgStartDte(dto: any): void{
