  popLetter(): void{
    let claimItem = null;
    let itemName = "";
    let itemType = "";
    let claimItemId = "";

    const claimItemObjList:any[] = [];
    const claimItemList = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    if(claimItemList.length>0){
      switch( this.seHardCopyDetails?.headerDto?.claimOption){
        case '1':{
          //refering to main item type
          itemType = 'AVE';
          claimItem = claimItemList?.controls.filter(x=> x.get('itemType').value === 'AVE')[0];
          break;
        }
        case '2':{
          itemType = 'ITX';
          claimItem = claimItemList?.controls.filter(x=> x.get('itemType').value === 'ITX')[0];
          break;
        }
        case '3':{
          itemType = 'REP';
          claimItem = claimItemList?.controls.filter(x=> x.get('itemType').value === 'REP')[0];
          break;
        }

      }
      claimItemList?.controls.forEach(x=>{
        claimItemObjList.push({
          itemType: x.get('itemType').value,
          itemName: x.get('itemName').value,
          claimItemId: x.get('claimItemId').value,
          latestItemStatusCode: x.get('latestStatus').value,
          latestItemStatusDesc: x.get('latestStatusDesc').value
        })
      })
      console.log("the outer claim item obj", claimItemObjList)

     
    }
    itemName = claimItem.get('itemName').value;
    claimItemId = claimItem.get('claimItemId').value;
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
      claimId: this.seHardCopyDetails?.claimId,
      claimType: 'S',
      itemType,
      itemName,
      claimItemId,
      latestStatus: this.seHardCopyDetails.latestStatus,
      latestStatusDesc: this.seHardCopyDetails.latestStatuDesc,
      trgStartDte: this.seHardCopyDetails.trgStartDte,
      trgEndDte: this.seHardCopyDetails.trgEndDte,
      verifier: this.seHardCopyDetails.verifier,
      verifierInit: this.seHardCopyDetails.verifierInit
    }
    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/pop-up/claim-letter', {claimItemObjList: JSON.stringify(claimItemObjList)}],{queryParams: params2},
    ));
    console.log(url);
    window.open(url, "viewClaimLetter", 'location=0,menubar=0,status=0,height=1000, width=1500');
  }

  openPopup(event:any, type: string): void{
    this.nspConfirmService.onConfirmPopup(event, type, null,
      () => {

        switch (type) {
          case 'confirmNoActionReq':
            this.noActionReq();
            break;
        }
      }
    );
  }

  noActionReq(): void{
    console.log("no action required");
    let params = new HttpParams();
    params = params.append('claimId',this.seHardCopyDetails?.claimId);
    params = params.append('claimType',this.claimType);
    this.claimService.noActionReq({},{params}).subscribe(resp => {
      if (resp && resp.ok) {
        if (resp.body.data) {
          this.toastService.success(resp.body.data);
        }
      }
    },
    (error: any) => {
      console.log("error is" + String(error?.error?.error_desc));
      this.toastService.error(String(error?.error?.error_desc));
    });

  }

  checkAllExpanded():void{
    this.allExpanded = this.accordionStates.every(a => a === true);
  }

  expandCollapseAll(): void{
    if(this.allExpanded){
      this.accordionStates.fill(false,0,8);
    } else {
      this.accordionStates.fill(true,0,8);
    }
    this.checkAllExpanded();
  }

  openDialog(event: Event, type: string):void{
    console.log("btn is" + type);

    const recompStatus = ["PRA"];
    const closedTypeStatus = ["COR", "CNS", "CCO"];
    const rejByCertStatus = ["RCE"];
    const rejByApprStatus = ['RAP'];
    const rejByRecApprStatus = ['RRA'];
    const rejBy2ndApprStatus = ['R2A'];
    const currentItemLatestStatuses:any[] = 
    Array.from(this.itemDetailsSection.itemStatusChanges.values()).filter((statusVal: any) => statusVal.afterStatus !== null && statusVal.afterStatus!== statusVal.beforeStatus).map(x=>{
      return String(x.afterStatus);
    });

    if(type === 'confirmSave' && !((closedTypeStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))) 
    && !(recompStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))
    && !(rejByCertStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))
    && !(rejByApprStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))
    && !(rejByRecApprStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))
    && !(rejBy2ndApprStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))
    ){
      console.log("lola")
      this.nspConfirmService.onConfirmPopup(event, type, null,
        () => {
          console.log("called api");
          this.saveClaim(null, null);
        }
      );
    }else{
      if(type === 'confirmSave' && (closedTypeStatus.some((status: any)=> currentItemLatestStatuses.includes(status)) || 
      rejByCertStatus.some((status: any)=> currentItemLatestStatuses.includes(status)) ||
      rejByApprStatus.some((status: any)=> currentItemLatestStatuses.includes(status)) || 
      rejByRecApprStatus.some((status: any)=> currentItemLatestStatuses.includes(status)) || 
      rejBy2ndApprStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))
      ){
        this.remarksDialogDisplay.set("closeRej", true);
      }
      if(type === 'confirmSave' && (recompStatus.some((status: any)=> currentItemLatestStatuses.includes(status)))){
        this.remarksDialogDisplay.set("recomp", true);
      }
    }

    if(type === 'closeRej' || type === 'recomp'){
      this.nspConfirmService.onConfirmPopup(event, type, null,
          () => {
    
            switch (type) {
              case 'closeRej':
                if(this.remarksDialogDisplay.get("closeRej")){
                  this.remarksDialogDisplay.set("closeRej", false);
                }
                break;
              case 'recomp':
                if(this.remarksDialogDisplay.get("recomp")){
                  this.remarksDialogDisplay.set("recomp", false);
                }
                break;
            }
            let callApi = false;
            for(const [key,value] of this.remarksDialogDisplay){
              console.log(`Map key is:${key} and value is:${String(value)}`);
              if(value){
                callApi = true;
                break;
              }
            }
            if(!callApi){
              console.log("called api");
              this.saveClaim(this.remarksForm.get('closedRejectedRemarks').value, this.remarksForm.get('recompRemarks').value);
            }
          }
        );
    }
  }

  tester(): void{
    // console.log("the item form array", this.itemDetailsSection.otherSEItemArr);
    console.log("the menu", this.nextButtonOptions)

  }

  //user must have claim approver role for cert and approval stage 
  validateRoleAndStage(){
    if(this.params.station === NSPAY2_STATION.STATION_CERTIFICATION || 
      this.params.station === NSPAY2_STATION.STATION_APPROVAL){
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER)){
        //do nothing
        console.log("route correct")

      }else {
        //temporary
        console.log("route to 500")
        const err: HttpErrorResponse = {
          name: 'HttpErrorResponse',
          message: '',
          error: "Unauthorized",
          ok: false,
          headers: new HttpHeaders,
          status: 0,
          statusText: '',
          url: '',
          type: HttpEventType.ResponseHeader
        }
        this.router.navigate(['/error/' + '500'], err.error);
      }
    }
  }

  retrieveConflictApprovedSEClaim():void{
    let params = new HttpParams();
    params = params.append("claimId",this.seHardCopyDetails?.claimId);
    params = params.append("nric",this.seHardCopyDetails?.headerDto.nsManNric);

    this.seClaimService.retrieveConflictApprovedSEClaim({params}).subscribe((resp:any) => {
      if(resp.status === 200 && resp.body.data){
        this.hasConflictAppClaims = resp.body.data;
    }},
    (error: any) => {
      console.log("error is" + String(error?.error?.error_desc));
      this.toastService.error(String(error?.error?.error_desc));
    });
  }
}
