                <div class="flex align-items-center">
                    Due:
                </div>
                &nbsp;&nbsp;
                <div class="flex align-items-center">
                    <h2 [style]="{'text-align': 'center','margin':'0'}">{{ dateUtilsService.convertDateToStr(dateUtilsService.convertStrToDate(seHardCopyDetails?.headerDto?.dueDate, "DD-MM-YYYY"), dueDateFormat) }}</h2>
                </div>
            </div>
        </div>
    </p-toolbar>
    <div class = "grid m-1">

        <!-- <div class = "col-6 nsp-align-left display-inline-block flex align-items-center" > -->
        <div class = "col-6 justify-content-start gap-4 flex align-items-center" >
          <div *ngIf ="seHardCopyDetails?.eClaimId"><a href='javascript:' (click)="popUpeClaim()">eClaim</a></div>
          <div><a href='javascript:' (click)="popUpPastClaim()">Past Claims</a></div>
          <!--TODO: when enquiry is ready with this screen-->
          <div><a href='#'>Enquiry CPF/IRAS</a></div>
          <div><a href='javascript:' (click)="popIras()">IRAS</a></div>
          <div><a href='javascript:' (click)="popUpLatestOwners()">Owners</a></div>
          <div><a href='javascript:' (click)="popSerbReport()">SERB Report</a></div>
          <div><a href='javascript:' (click)= "popRemarks()">Remarks</a></div>
          <div><a href='#'>Prepopulated</a></div>
        </div>
        <div class = "col-6 flex justify-content-end gap-2">

        <form [formGroup] = "buttonForm">
            <button pButton (click) = "expandCollapseAll()">{{this.allExpanded? "Collapse All": "Expand All"}}</button>
            <button pButton (click) = "openPopup($event,'confirmNoActionReq')" label="No action required"
            *ngIf = "seHardCopyDetails?.latestStatus === 'COM' && params.reason === 'Supp Doc' && !readOnly"></button>
            <button pButton label="Letter" (click)= "popLetter()"></button>
            <ng-container *ngIf="nextButtonOptions.length<=0 || readOnly; else hasNextButton">
            </ng-container>
            <ng-template #hasNextButton>
                <ng-container *ngIf="nextButtonOptions.length>1; else singleNextButton">
                    <p-splitButton *ngIf="nextButtonOptions.length>1; else singleNextButton" [label]="nextButtonOptions[0].label"
                        (onClick)="nextButtonOptions[0].command()" [model]="filteredNextButtonOptions()"></p-splitButton>
                </ng-container>
            </ng-template>
            <ng-template #singleNextButton>
                <button pButton [label]="nextButtonOptions[0].label" (onClick)="nextButtonOptions[0].command()"></button>
            </ng-template>
            <!-- <button pButton label="Save" (click)="saveClaim()"></button> -->
            <button pButton *ngIf = "!readOnly" label="Save" (click)="openDialog($event, 'confirmSave')" [disabled] ="!isValidAllForms()" ></button>
            <button pButton label="TESTER" (click)="tester()"></button>
        </form>
        </div>
    </div>
</div>
<!--consolidated p messages in sticky header-->

<p-messages severity="warn" *ngIf = claimSection.isLongPeriod()>
    <ng-template key="CLM-W003" pTemplate >
        <div class="ml-2">{{ claimSection.claimJson.nspay2App.claim.warning['CLM-W003'] }}</div>
    </ng-template>
</p-messages>
<p-messages severity="warn" *ngIf = "dateUtilsService.isAfter(claimSection.claimForm?.get('commencementDte').value, claimSection.claimForm?.get('trgStartDte').value)">
    <ng-template key="CLM-W001" pTemplate >
        <div class="ml-2">{{ claimJson.nspay2App.claim.warning['CLM-W001'] }}</div>
    </ng-template>
</p-messages>
<p-messages severity="warn" *ngIf = "claimSection.SOLE_PROP_PARTNER_CODES.includes(claimSection.claimForm?.get('occupation').value)">
    <ng-template key="CLM-W009" pTemplate >
        <div class="ml-2">{{ claimJson.nspay2App.claim.warning['CLM-W009'] }}</div>
    </ng-template>
</p-messages>
<p-messages severity="warn" *ngIf = claimSection.isLateClaim()>
    <ng-template key="CLM-W004" pTemplate >
        <div class="ml-2">{{ claimJson.nspay2App.claim.warning['CLM-W004'] }}</div>
    </ng-template>
</p-messages>
<p-messages severity="warn" *ngIf="claimSection.workHourStartMoreThanEnd()">
    <ng-template key="CLM-W006" pTemplate >
        <div class="ml-2">{{ claimJson.nspay2App.claim.warning['CLM-W006'] }}</div>
    </ng-template>
</p-messages>

                      <!-- (restDayEvent)="getRestDay($event)" -->

<p-accordion>
    <n2-claim-section #claimSection
                      [seHardCopyDetails]="seHardCopyDetails"
                      [itemDetailsSection]="itemDetailsSection"
                      (saveClaimSectionEvent)="getClaimSectionDto($event)"
                      (trgStartDteEvent) ="getTrgStartDte($event)"
                      [saveSEClaimRequest] = "saveSEClaimRequest"
                      (click) = "checkAllExpanded()"
                      [(selected)] = "accordionStates[0]"
                      [readOnly] = "readOnly"
                      [station] = "station">
    </n2-claim-section>
    <n2-iras-dld-details-section [seHardCopyDetails]="seHardCopyDetails"
                                 (irasDetailsEvent)="getIrasDetails($event)"
                                 (click) = "checkAllExpanded()"
                                 [(selected)] = "accordionStates[1]">
    </n2-iras-dld-details-section>
    <n2-working-data-section #workingDataSection
                             [seHardCopyDetails]="seHardCopyDetails"
                             [itemDetailsSection]="itemDetailsSection"
                             (totalAmtPayEvent)="getTotalAmtPay($event)"
                             [saveSEClaimRequest] = "saveSEClaimRequest"
                             [trgStartDte] = "trgStartDte"
                             [irasDetails]="irasDetails"
                             (click) = "checkAllExpanded()"
                             [(selected)] = "accordionStates[2]"
                             [readOnly] = "readOnly"
                             [station] = "station">
    </n2-working-data-section>
    <n2-item-details-section #itemDetailsSection
                             [seHardCopyDetails]="seHardCopyDetails"
                             [totalAmtPay]="totalAmtPay"
                             (recomputeMatchedAttdEvent)="getMatchAttd($event)"
                             [saveSEClaimRequest] = "saveSEClaimRequest"
                             (click) = "checkAllExpanded()"
                             [(selected)] = "accordionStates[3]"
                             [readOnly] = "readOnly"
                             [station] = "station"
                             [hasConflictAppClaims] = "hasConflictAppClaims">
    </n2-item-details-section>
  <n2-serb-approval-section #serbApprovalSection
                            [saveSEClaimRequest] = "saveSEClaimRequest"
                            [seHardCopyDetails]="seHardCopyDetails"
                            (click) = "checkAllExpanded()"
                            [(selected)] = "accordionStates[4]"
                            [readOnly] = "readOnly"
                            [station] = "station">
  </n2-serb-approval-section>
  <n2-serb-rationale-amendment-section #serbRationaleAmendmentSection
                            [saveSEClaimRequest] = "saveSEClaimRequest"
                            [seHardCopyDetails]="seHardCopyDetails"
                            (click) = "checkAllExpanded()"
                            [(selected)] = "accordionStates[5]"
                            [itemDetailsSection]="itemDetailsSection"
                            [readOnly] = "readOnly"
                            [station] = "station"
                            [hasConflictAppClaims] = "hasConflictAppClaims">
  </n2-serb-rationale-amendment-section>
    <n2-item-change-history-section  [seHardCopyDetails]="seHardCopyDetails"
                                     (click) = "checkAllExpanded()"
                                     [(selected)] = "accordionStates[6]">
    </n2-item-change-history-section>
    <n2-actual-training-details-section #actualTrainingDetailsSection
                                        [seHardCopyDetails]="seHardCopyDetails"
                                        [matchedAttd]="matchedAttd"
                                        [saveSEClaimRequest] = "saveSEClaimRequest"
                                        (click) = "checkAllExpanded()"
                                        [(selected)] = "accordionStates[7]"
                                        [readOnly] = "readOnly"
                                        [station] = "station"
                                        [hasConflictAppClaims] = "hasConflictAppClaims">
    </n2-actual-training-details-section>
</p-accordion>
<p-confirmPopup key = "confirmSave" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>
<p-confirmPopup key = "confirmNoActionReq" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>


<p-dialog header="Remarks" [visible]="remarksDialogDisplay.get('closeRej')"
[modal]="true" [style]="{width: '50vw'}" [draggable]="false" [resizable]="false" [formGroup]="remarksForm">
    <h6>Please key in remarks for closing/rejecting items</h6>
    <textarea rows="5"
    class="inputfield w-full"
    inputId = "remark"
    formControlName = "closedRejectedRemarks"
    maxlength="450"
    pInputTextarea
    ></textarea>
    <ng-template pTemplate="footer">
        <button pButton label="Cancel" type="button" (click)="remarksDialogDisplay.set('closeRej', false)" class="p-button-danger"></button>
        <button pButton label="Submit" type="button" (click)="openDialog($event, 'closeRej')"
        [disabled]="remarksForm.get('closedRejectedRemarks')?.hasError('required')"></button>
    </ng-template>
</p-dialog>
<p-confirmPopup key = "closeRej" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>

<p-dialog header="Remarks" [visible]="remarksDialogDisplay.get('recomp')"
[modal]="true" [style]="{width: '50vw'}" [draggable]="false" [resizable]="false" [formGroup]="remarksForm">
<h6>Please key in remarks for recomputation</h6>
<textarea rows="5"
    class="inputfield w-full"
    inputId = "remark"
    formControlName = "recompRemarks"
    maxlength="450"
    pInputTextarea
    ></textarea>
    <ng-template pTemplate="footer">
        <button pButton label="Cancel" type="button" (click)="remarksDialogDisplay.set('recomp', false)" class="p-button-danger"></button>
        <button pButton label="Submit" type="button" (click)="openDialog($event, 'recomp')"
        [disabled]="remarksForm.get('recompRemarks')?.hasError('required')"></button>
    </ng-template>
</p-dialog>
<p-confirmPopup key = "recomp" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>
