import {Component, Inject, Input, LOCALE_ID, OnInit, ViewChild} from '@angular/core';
import {LazyLoadEvent} from 'primeng/api';
import {ProtectedPayableService} from "app/payment/protected-payable/service/protected-payable.service";
import {HttpErrorResponse, HttpResponse} from "@angular/common/http";
import {Ns2HttpService} from "app/shared/service/ns2-http.service";
import {IProtectedPayable} from "app/payment/protected-payable/model/protected-payable.model";
import {Table} from "primeng/table";
import {CustomToastService} from "app/layouts/service/customtoast.service";
import {NspConfirmationService} from "app/shared/confirm/nsp-confirm.service";
import {N2Status} from "app/shared/model/ns2status-enum.model";
import { formatDate } from '@angular/common';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';


@Component({
  selector: 'n2-protected-payable-tab',
  templateUrl: './protected-payable-tab.component.html',
  styleUrls: ['./protected-payable-tab.component.scss']
})
export class ProtectedPayableTabComponent implements OnInit {

  @Input() selectedId = "";

  protectedPayableList: IProtectedPayable[];

  // Enumeration object for decoding common NSPAY2.0 statuses
  n2StatusEnum: { [key: string]: N2Status } = N2Status

  // Lazy loading
  loading: boolean;
  totalRecords: number;
  selectableRecords: number;
  pageData: { currentPage: number } = { 
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent: LazyLoadEvent;

  // Selection checkbox handling
  selectAll = false;
  selectedProtectedPayableList: IProtectedPayable[] = [];
  selectableRowsExist = false;

  @ViewChild('dt') table: Table;

  constructor(
    private protectedPayableService: ProtectedPayableService,
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private ns2HttpService: Ns2HttpService,
    private exportUtilsService: ExportUtilsService,
    private dateUtilsService: DateUtilsService,
    @Inject(LOCALE_ID) protected localeId: string
  ) {
  }

  ngOnInit(): void {
    this.protectedPayableList = [];
    this.loading = false;
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  loadProtectedPayables(event: LazyLoadEvent): void {
    this.loading = true;
    this.lazyLoadEvent = event;
    setTimeout(() => {
      const options = this.ns2HttpService.convertLazyEvent(event, this.pageData, this.pageSize);

      this.protectedPayableService.getProtectedPayablesByNSman(this.selectedId, options).subscribe(
        (res: HttpResponse<any>) => {
          if (res.body?.data?.content) {
            this.protectedPayableList = res.body.data.content;
            this.selectableRecords = this.protectedPayableList.filter(value => this.n2StatusEnum[value.status] === N2Status.ONGOING).length;
            this.selectableRowsExist = this.selectableRecords > 0;
            this.totalRecords = res.body.data.totalElements;
          } else {
            this.protectedPayableList = [];
            this.totalRecords = 0;
            this.selectableRowsExist = false;
            this.toastService.info('No records found');
          }
          this.loading = false;
        },
        (error: HttpErrorResponse) => {
          // eslint-disable-next-line no-console
          console.log(error.message);
        }
      );
    }, 1000);
  }

  onSelectionChange(value: any[] = []): void {
    this.selectAll = value.length === this.selectableRecords;
    this.selectedProtectedPayableList = value;
  }

  onSelectAllChange(event: any): void {
    const checked = event.checked;
    if (checked) {
      this.selectedProtectedPayableList = this.protectedPayableList.filter(value => this.n2StatusEnum[value.status] === N2Status.ONGOING );
      this.selectAll = true;
      console.log(this.selectedProtectedPayableList);
    } else {
      this.selectedProtectedPayableList = [];
      this.selectAll = false;
    }
  }

  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;
    // Reset selection when loading another page
    this.selectedProtectedPayableList = [];
    this.selectAll = false;
  }

  applyFilter(column: string, filterValue: string): void {
    this.table.filter(filterValue, 'status', 'equals');
    this.pageData.currentPage = 0;
    this.pageSize = 10;
  }

  resetDataTable(): void {
    this.selectAll = false;
    this.selectedProtectedPayableList = [];
    this.table.filters = {};
    this.pageData.currentPage = 0;
    this.pageSize = 10;
    this.table.reset();
  }

  downloadSupportingDocuments(protectedPayableId: number): void {
    console.log(protectedPayableId);
    // TODO: Implement the download of the supporting docs, given the PP ID
  }

  confirmTerminateSelection(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      this.terminateSelection();
    });
  }

  terminateSelection(): void {
    this.protectedPayableService.terminate(this.selectedProtectedPayableList).subscribe(
      (res: HttpResponse<any>) => {
        if (res.body?.data) {
          this.toastService.success(res.body?.success_message);
          this.resetDataTable();
        }
      },
      (error: HttpErrorResponse) => {
        console.log(error.message);
      });
  }

  //Exprot CSv
  exportCSV(dataTable: Table): void {
    const options = this.ns2HttpService.convertLazyEvent(this.lazyLoadEvent, 0, this.totalRecords);

    this.protectedPayableService.getProtectedPayablesByNSman(this.selectedId, options).subscribe(
      resp => {
        if(resp.body?.data?.content) {

          const data = resp.body?.data?.content;

          const columns =
          [
            {field: 'sn', header: 'SN', index: 0},
            {field: 'transactionId', header: 'Transaction Id', index: 1},
            {field: 'protectedAmt', header: 'Protected Amount', index: 2},
            {field: 'effectiveDt', header: 'Effective Date', index: 3},
            {field: 'createdBy', header: 'Created By', index: 4},
            {field: 'approvedBy', header: 'Approved By', index: 5},
            {field: 'terminateBy', header: 'Terminated By', index: 6},
            {field: 'terminateApprovedBy', header: 'Terminate Approved By', index: 7},
            {field: 'status', header: 'Status', index: 8},
          ];

          const dtoList:any[] = [];
          console.log("data", data);
          data.forEach((d: IProtectedPayable, index: number) => {
            const dto = {
              sn: index + 1,
              transactionId: d.transactionId,
              protectedAmt: d.protectServicePayInd === 'Y' ? 'Service Pay' : d.protectedAmt,
              effectiveDt:`${String(this.dateUtilsService.formatStrDate(d.submissionReviewedDt, "DD-MM-YYYY (HHmm)"))}`,
              createdBy: `${String(d.submittedBy ? d.submittedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.submittedDt, "DD-MM-YYYY (HHmm)"))}`,
              approvedBy: `${String(d.submissionReviewedBy ? d.submissionReviewedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.submissionReviewedDt, "DD-MM-YYYY (HHmm)"))}`,
              terminateBy: `${String(d.terminationRequestedBy ? d.terminationRequestedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.terminationRequestedDt, "DD-MM-YYYY (HHmm)"))}`,
              terminateApprovedBy: `${String(d.terminationReviewedBy ? d.terminationReviewedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.terminationReviewedDt, "DD-MM-YYYY (HHmm)"))}`,
              status: this.n2StatusEnum[d.status],
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList,resp.body.data.total_no_of_elems, columns,
            "data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }
}
<p-table
    #dt
    [value]="protectedPayableList"
    [selection]="selectedProtectedPayableList"
    (selectionChange)="onSelectionChange($event)"
    [selectAll]="selectAll"
    (selectAllChange)="onSelectAllChange($event)"
    styleClass="p-datatable-gridlines"
    [paginator]="true"
    [rows]="10"
    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
    [rowsPerPageOptions]="[10,25,50]"
    [showCurrentPageReport]="true"
    [lazy]="true"
    [totalRecords]="totalRecords"
    [loading]="loading"
    (onLazyLoad)="loadProtectedPayables($event)"
    (onPage) = "onPage($event)">
    <ng-template pTemplate="caption">
        <div class="flex justify-content-between">
          <div>
            <button type="button" pButton pRipple icon="pi pi-file" (click)="exportCSV(dt)" pTooltip="CSV" tooltipPosition="bottom"></button>
            <button class='p-button-outlined' type='button' pButton label='Ongoing' (click)="applyFilter('status', 'ONGOING')" [style]="{'margin-left': '.5em'}"></button>
            <button class='p-button-outlined' type='button' pButton label='Pending Approval' (click)="applyFilter('status', 'PENDING_APPROVAL')" [style]="{'margin-left': '.5em'}"></button>
            <button class='p-button-outlined' type='button' pButton label='Rejected' (click)="applyFilter('status', 'REJECTED')" [style]="{'margin-left': '.5em'}"></button>
            <button class='p-button-outlined' type='button' pButton label='Terminated' (click)="applyFilter('status', 'TERMINATED')" [style]="{'margin-left': '.5em'}"></button>
            <button class='p-button-outlined' type='button' pButton label='All' (click)='resetDataTable()' [style]="{'margin-left': '.5em'}"></button>
          </div>
          <div style="padding: 0" class='col-6 flex justify-content-end'>
            <button type='button' pButton pRipple label='Terminate' (click)='confirmTerminateSelection($event, "terminateProtectedPayableRecords",null)' pTooltip='Terminate Protected Payable'
                    tooltipPosition='bottom' [disabled]="selectedProtectedPayableList?.length === 0"></button>
            <p-confirmPopup key='terminateProtectedPayableRecords'></p-confirmPopup>
          </div>
        </div>
    </ng-template>
    <ng-template pTemplate="header">
        <tr>
            <th scope="col" style="width:3%"><p-tableHeaderCheckbox *ngIf="selectableRowsExist"></p-tableHeaderCheckbox></th>
            <th scope="col" style="width: 5%">SN</th>
            <th scope="col" pSortableColumn="transactionId">Transaction ID<p-sortIcon field="transactionId"></p-sortIcon></th>
            <th scope="col" pSortableColumn="protectedAmt">Protected Amount<p-sortIcon field="protectedAmt"></p-sortIcon></th>
            <th scope="col" pSortableColumn="submittedDt">Effective Date<p-sortIcon field="submittedDt"></p-sortIcon></th>
            <th scope="col" pSortableColumn="submittedDt">Created By<p-sortIcon field="submittedDt"></p-sortIcon></th>
            <th scope="col" pSortableColumn="submissionReviewedDt">Approved By<p-sortIcon field="submissionReviewedDt"></p-sortIcon></th>
            <th scope="col" pSortableColumn="terminationRequestedDt">Terminated By<p-sortIcon field="terminationRequestedDt"></p-sortIcon></th>
            <th scope="col" pSortableColumn="terminationReviewedDt">Terminate Approved By<p-sortIcon field="terminationReviewedDt"></p-sortIcon></th>
            <th scope="col" pSortableColumn="status">Status<p-sortIcon field="status"></p-sortIcon></th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-protectedPayableRecord let-i="rowIndex">
        <tr>
          <td><p-tableCheckbox *ngIf="protectedPayableRecord?.status === 'ONGOING'" [value]="protectedPayableRecord"></p-tableCheckbox></td>
          <td>{{i + 1}}</td>
          <td>{{protectedPayableRecord?.transactionId | defaultString}}</td>
          <td *ngIf="protectedPayableRecord?.protectServicePayInd == 'N'">{{protectedPayableRecord?.protectedAmt | defaultString }}</td>
          <td *ngIf="protectedPayableRecord?.protectServicePayInd == 'Y'">Service Pay</td>
          <td><div class='flex justify-content-between'>
            {{protectedPayableRecord?.submissionReviewedDt | date:'dd-MM-yyyy' | defaultString}}
            <button type='button' pButton pRipple icon='pi pi-file-o' (click)='downloadSupportingDocuments(protectedPayableRecord.id)'
                    pTooltip='Download Supporting Documents'
                    tooltipPosition='bottom'></button>
          </div></td>
          <td>{{protectedPayableRecord?.submittedBy | defaultString}} <br *ngIf="protectedPayableRecord?.submittedBy"> {{protectedPayableRecord?.submittedDt | date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
          <td>{{protectedPayableRecord?.submissionReviewedBy | defaultString}} <br *ngIf="protectedPayableRecord?.submissionReviewedDt"> {{protectedPayableRecord?.submissionReviewedDt | date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
          <td>{{protectedPayableRecord?.terminationRequestedBy | defaultString}} <br *ngIf="protectedPayableRecord?.terminationRequestedDt"> {{protectedPayableRecord?.terminationRequestedDt | date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
          <td>{{protectedPayableRecord?.terminationReviewedBy | defaultString}} <br *ngIf="protectedPayableRecord?.terminationReviewedDt"> {{protectedPayableRecord?.terminationReviewedDt | date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
          <td>{{n2StatusEnum[protectedPayableRecord?.status] | defaultString}}</td>
        </tr>
    </ng-template>

    <ng-template pTemplate='emptymessage'>
      <tr>
        <td colspan='10'>No Protected Payable records found.</td>
      </tr>
    </ng-template>
</p-table>
