import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { LocalStorageService } from 'ngx-webstorage';
import { AccountService } from 'app/core/auth/account.service';
import { Account } from 'app/core/auth/account.model';

@Component({
  selector: 'ic-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit, OnDestroy {
  account: Account | null = null;
  authSubscription?: Subscription;

  constructor(private accountService: AccountService, private $localStorage: LocalStorageService, private router: Router) {}

  ngOnInit(): void {
    this.authSubscription = this.accountService.getAuthenticationState().subscribe(account => this.storePassRules(account));
  }

  isAuthenticated(): boolean {
    return this.accountService.isAuthenticated();
  }

  login(): void {
    this.router.navigate(['/login']);
  }

  ngOnDestroy(): void {
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
    }
  }

  storePassRules(account: Account): void {
    this.account = account;
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-condition
    if (this.account) {
      if (
        !this.$localStorage.retrieve('minPasswordLength') ||
        !this.$localStorage.retrieve('maxPasswordLength') ||
        !this.$localStorage.retrieve('passwordRule')
      ) {
        this.$localStorage.store('minPasswordLength', account.minPasswordLength);
        this.$localStorage.store('maxPasswordLength', account.maxPasswordLength);
        this.$localStorage.store('passwordRule', account.passwordRule);
      }
    }
  }
}
