  constructConsolidatedHeaderData(dataList, consolidated, empty, newForm) {
    //logic
    console.log("before data", dataList);
    //12 columns of 12 mths from claimstartdate/trgStartDte - 13 mths to trgStartDte - 1 mths
    const sanitizedDataList = [];
    let sortedData;
    if (empty || newForm || consolidated) {
      console.log("before yrmthlist", this.yrmthList);
      this.colMthRange.forEach((mth, colIndex) => {
        dataList.push({
          yrmthReCon: (0, moment_1.default)(this.dateUtilsService.convertStrToDate(mth, "DD-MM-YYYY")).format("MM-YYYY")
        });
      });
      sortedData = dataList;
    } else {
      this.yrmthList.forEach(yrmth => {
        for (let i = 0; i < dataList.length; i++) {
          if (dataList[i].yrmth === yrmth && sanitizedDataList.findIndex(x => x.yrmth === yrmth) < 0) {
            sanitizedDataList.push(dataList[i]);
          }
        }
      });
      this.yrmthList.forEach(yrmth => {
        const index = sanitizedDataList.findIndex(x => x.yrmth === yrmth);
        if (index < 0) {
          sanitizedDataList.push({
            yrmthReCon: yrmth
          });
        } else {
          sanitizedDataList[index].yrmthReCon = yrmth;
        }
      });
      console.log("after data", sanitizedDataList);
      sortedData = sanitizedDataList.sort((a, b) => (0, moment_1.default)(a.yrmthReCon, "MM-YYYY") < (0, moment_1.default)(b.yrmthReCon, "MM-YYYY") ? -1 : 1);
    }
    console.log("sorted data is", sortedData);
    return sortedData;
  }
