  static mapInsurAveWDToFormGroup(insurAveWD: InsurAveWD, formBuilder: FormBuilder, dateUtilsService: DateUtilsService): FormGroup{
    const insurAveFormGroup: FormGroup = formBuilder.group({
      workingDataId: new FormControl(insurAveWD.workingDataId),
      id: new FormControl(insurAveWD.id, null),
      consolidated: new FormControl(insurAveWD.consolidated, null),
      eligibleMthCount: new FormControl(insurAveWD.eligibleMthCount, null),
      prdnBonusDivisor: new FormControl(insurAveWD.prdnBonusDivisor, null),
      incomeTaxInsurArrayMthly: formBuilder.group({}),
      incomeTaxInsurArrayConsol: formBuilder.group({}),
      captionGroup: formBuilder.group({
        radioOption: new FormControl(insurAveWD.consolidated? "CON": "MTH", null)
      }),
      footerGroup:formBuilder.group({
        claimNip: new FormControl(insurAveWD.claimNip, null)
      }),
      annualIncomeList: new FormArray([]),
      yrmthList: new FormControl(null, null),
      startYrmth: new FormControl('', null),
      endYrmth: new FormControl('', null),
    });
    InsuranceAvgIncomeComponent.staticGenerateYrMth(insurAveWD, dateUtilsService, 
      insurAveFormGroup.get('yrmthList'), 
    insurAveFormGroup.get('startYrmth'), insurAveFormGroup.get('endYrmth'));
    let dataRecon:any[] = [];
    const newDataList:any[] = [];

    if(insurAveWD.consolidated === true){
      //expect 1
      if(insurAveWD.dataList){
        insurAveFormGroup.get('captionGroup.radioOption').setValue('CON');

        // construct populated for consolidated view
        dataRecon = InsuranceAvgIncomeComponent.staticConstructConsolidatedHeaderData(insurAveWD.dataList, true, false, false, insurAveFormGroup.get('yrmthList').value);
        InsuranceAvgIncomeComponent.staticConstructForm(dataRecon, insurAveFormGroup.get('incomeTaxInsurArrayConsol') as FormGroup, formBuilder, insurAveFormGroup);

        // construct empty for mthly view
        dataRecon = InsuranceAvgIncomeComponent.staticConstructConsolidatedHeaderData(newDataList, false, true, false,insurAveFormGroup.get('yrmthList').value);
        InsuranceAvgIncomeComponent.staticConstructForm(newDataList, insurAveFormGroup.get('incomeTaxInsurArrayMthly') as FormGroup, formBuilder, insurAveFormGroup);
      }
    }else{
      //list of mthly records
      if(insurAveWD.dataList){
        insurAveFormGroup.get('captionGroup.radioOption').setValue('MTH');

        // construct populated for mthly view
        dataRecon = InsuranceAvgIncomeComponent.staticConstructConsolidatedHeaderData(insurAveWD.dataList, false, false, false, insurAveFormGroup.get('yrmthList').value);
        InsuranceAvgIncomeComponent.staticConstructForm(dataRecon, insurAveFormGroup.get('incomeTaxInsurArrayMthly') as FormGroup, formBuilder, insurAveFormGroup);

        //construct empty for consolidated view
        dataRecon = InsuranceAvgIncomeComponent.staticConstructConsolidatedHeaderData(newDataList, true, true, false, insurAveFormGroup.get('yrmthList').value);
        InsuranceAvgIncomeComponent.staticConstructForm(newDataList, insurAveFormGroup.get('incomeTaxInsurArrayConsol') as FormGroup, formBuilder, insurAveFormGroup);
      }
    }
    insurAveFormGroup.get('footerGroup.claimNip').setValue(insurAveWD?.claimNip);

    //set eligible mth divisor and prdn bonus divisor
    insurAveFormGroup.get('eligibleMthCount').setValue(insurAveWD?.eligibleMthCount);
    insurAveFormGroup.get('prdnBonusDivisor').setValue(insurAveWD?.prdnBonusDivisor);
    insurAveFormGroup.get('consolidated').setValue(insurAveWD?.consolidated);

    insurAveWD.annualIncomeList.forEach((annualIncomeWD: AnnualIncomeWD) => {
      const annualIncomeFormGroup: FormGroup = AnnualIncomeComponent.mapAnnualIncomeWDToFormGroup(annualIncomeWD, formBuilder, dateUtilsService);
      (insurAveFormGroup.get('annualIncomeList') as FormArray).push(annualIncomeFormGroup);
    });
    return insurAveFormGroup;
  }

  static staticGenerateYrMth(dataSet: any, dateUtilsService: DateUtilsService, yrmthListCtrl: AbstractControl, startYrmthCtrl:AbstractControl, endYrmthCtrl: AbstractControl): void{
    let claimStartDt;
    let  claimEndDt;
    const colMthRange = [];
    let yrmthList:string[] = [];
    if(dataSet){
      const dateFormat = "DD-MM-YYYY";
      const claimStartYrMth:Date =  dateUtilsService.convertStrToDate(dataSet?.startYrmth,dateFormat);
      const claimEndYrMth:Date =  dateUtilsService.convertStrToDate(dataSet.endYrmth, dateFormat);
      claimStartDt = moment(new Date(claimStartYrMth.getFullYear(), claimStartYrMth.getMonth(),1));
      claimEndDt = moment(new Date(claimEndYrMth.getFullYear(), +claimEndYrMth.getMonth()+ 1, 0));
      const colStartDt = claimEndDt.clone().subtract(5, 'months');
      const colEndDt = claimEndDt.clone();
      for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
        colMthRange.push(mth.format("DD-MM-YYYY"));
      }
      yrmthList = colMthRange.map(x=>  moment(dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
    }
    const cloneYrmthList = JSON.parse(JSON.stringify(yrmthList))

    yrmthListCtrl.setValue(yrmthList);
    startYrmthCtrl.setValue("01-".concat(cloneYrmthList[0]));
    endYrmthCtrl.setValue("01-".concat(cloneYrmthList[cloneYrmthList.length -1]));
  }

