  static staticConstructConsolidatedHeaderData(dataList: any[], consolidated: boolean, empty: boolean, newForm:boolean, yrmthList: any[]): any[]{

     const sanitizedDataList:any[] =[];
     let sortedData;
     if(empty || newForm || consolidated){
       yrmthList.forEach((yrmth:any, colIndex:number)=>{
         dataList.push({yrmthReCon: yrmth});
       });
       sortedData = dataList;
     }else{
       yrmthList.forEach(yrmth =>{
         for(let i=0; i< dataList.length; i++){
           if(dataList[i].yrmth===yrmth && sanitizedDataList.findIndex(x=>x.yrmth === yrmth)<0){
             sanitizedDataList.push(dataList[i]);
           }
         }
       });
       yrmthList.forEach(yrmth =>{
         const index = sanitizedDataList.findIndex(x=> x.yrmth ===  yrmth);
         if(index<0){
           sanitizedDataList.push({yrmthReCon: yrmth});
         }else{
           sanitizedDataList[index].yrmthReCon = yrmth;
         }
       })
       sortedData = sanitizedDataList.sort((a:any, b:any)=>moment(a.yrmthReCon,"MM-YYYY") < moment(b.yrmthReCon,"MM-YYYY") ? -1 : 1);
     }
     return sortedData;
   }

