  async confirmSwitch(e: Event,  type: string) {
    const value = (e.target as HTMLInputElement).value;
    console.log("incoming value",value)
    if (value !== this.aveInsurForm.get('captionGroup.radioOption').value) {
      e.preventDefault();
      const confirm = await this.showConfirm(e,type);
      if (!confirm) {
        return;
      }else{
        this.aveInsurForm.get('captionGroup.radioOption').setValue(value);
        this.aveInsurForm.get('consolidated').setValue(value === 'CON')
        this.clearForm(value);
      }
    }
