  retrieveInsurWD():void{
    if((!this.currSelectedOption.changes && !this.currSelectedOption.newWD)) {

      let params = new HttpParams();
      params = params.append("workingDataId", this.currSelectedOption?.obj.workingDataId);
      this.aveInsurForm =  this.formBuilder.group({
        id: new FormControl(null, null),
        workingDataId: new FormControl('',null),
        consolidated: new FormControl(false, null),
        eligibleMthCount: new FormControl(6, null),
        prdnBonusDivisor: new FormControl(12, null),
        incomeTaxInsurArrayMthly: this.formBuilder.group({}),
        incomeTaxInsurArrayConsol: this.formBuilder.group({}),
        captionGroup: this.formBuilder.group({
          radioOption: new FormControl(this.radioOptions[0].key, null)
        }),
        footerGroup:this.formBuilder.group({
          claimNip: new FormControl(0, null)
        }),
        annualIncomeList: new FormArray([]),
        yrmthList:new FormControl([], null),
        startYrmth: new FormControl('', null),
        endYrmth: new FormControl('', null),
      });
      this.seClaimService.retrieveInsurWD({params}).subscribe((resp:any) => {
        if(resp){
            if(resp.status === 200 && resp.body.data){

              this.generateYrMth(resp.body.data);
              this.populateAveInsurForm(resp.body.data, false);
              this.valueChangeSubscribe();
            }
          }
        },
        (error: any) => {
          console.log("error is" + String(error?.error?.error_desc));
          this.toastService.error(String(error?.error?.error_desc));
        });


    }else if(this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      console.log("calling from exist")
      this.aveInsurForm = this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup as FormGroup;
      this.yrmthList = this.aveInsurForm.get('yrmthList').value;

      // this.generateYrMth(null);
      this.setRadioOption(this.aveInsurForm.get('consolidated').value);
      console.log("get from changes WD Map", this.aveInsurForm)
      this.loading = false;


    }else if(this.currSelectedOption.newWD) {
      console.log("get from new WD Map");
      console.log("selected key",this.currSelectedOption?.key.toString())
      this.aveInsurForm = this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup as FormGroup;
      if(this.aveInsurForm.get('consolidated')){
        this.setRadioOption(this.aveInsurForm.get('consolidated').value);
        //defaulted to monthly
      }else this.setRadioOption(false);
      this.loading = false;

    }
  }


