  populateAveInsurForm(dataSet: any, newForm: boolean): FormGroup{
    this.dataSet = dataSet;
    const newDataList: any[] = [];
    let dataRecon:any[] = [];
    if(this.dataSet.consolidated === true){
      //expect 1
      if(this.dataSet.dataList){
        this.aveInsurForm.get('captionGroup.radioOption').setValue('CON');

        // construct populated for consolidated view
        dataRecon = this.constructConsolidatedHeaderData(this.dataSet.dataList, true, false, newForm);
        this.constructForm(dataRecon, this.incomeTaxInsurArrayConsol);

        // construct empty for mthly view
        this.constructConsolidatedHeaderData(newDataList, false, true, newForm);
        this.constructForm(newDataList, this.incomeTaxInsurArrayMthly);
      }else{
        console.log("empty")
      }
    }else{
      //list of mthly records
      if(this.dataSet.dataList){
        this.aveInsurForm.get('captionGroup.radioOption').setValue('MTH');

        // construct populated for mthly view
        dataRecon = this.constructConsolidatedHeaderData(this.dataSet.dataList, false, false, newForm);
        this.constructForm(dataRecon, this.incomeTaxInsurArrayMthly);

        //construct empty for consolidated view
        this.constructConsolidatedHeaderData(newDataList, true, true, newForm);
        this.constructForm(newDataList, this.incomeTaxInsurArrayConsol);
      }
    }
    this.aveInsurForm.get('footerGroup.claimNip').setValue(dataSet?.claimNip);
    this.constructAnnualIncomeForm(dataSet.annualIncomeList);

    //set eligible mth divisor and prdn bonus divisor
    this.aveInsurForm.get('eligibleMthCount').setValue(dataSet.eligibleMthCount);
    this.aveInsurForm.get('prdnBonusDivisor').setValue(dataSet.prdnBonusDivisor);
    this.aveInsurForm.get('consolidated').setValue(this.dataSet.consolidated);

    this.loading = false;
    return this.aveInsurForm;
  }

