  getSumOfCurrRow(formCtrlName: string): number
  {
    let sum = 0;
    let fieldCtrl;
    this.getYrmthList().slice(this.maxMths - this.aveInsurForm?.get('eligibleMthCount').value, this.maxMths).forEach(yrmth => {//for each mth
      fieldCtrl = this.incomeTaxInsurArrayMthly?.get(yrmth)?.get(formCtrlName).value || 0; 
      // fieldCtrl is field level, i.e. firstYrCommission
      sum+=fieldCtrl;
    });
    return sum;
  }

