    Number(formArr.get(yrmthCtrlName).get('firstYrCommission').value || 0 )
    +  Number(formArr.get(yrmthCtrlName).get('singlePremium').value || 0 )
    +  Number(formArr.get(yrmthCtrlName).get('prdCommission').value || 0 )
    +  Number(formArr.get(yrmthCtrlName).get('generalInsurance').value || 0 )
    +  Number(formArr.get(yrmthCtrlName).get('otherCommission').value || 0 )
    +  Number(formArr.get(yrmthCtrlName).get('prdBonus').value || 0 );
    return sum;

