  getOverridingCommPerc(): number{
    let rslt = 0;
    if(this.captionGroup.get('radioOption').value==='MTH'){
      rslt = this.getSumOfCurrRow('overridingCommission')/(this.getSumOfTotalNonPayable() + this.getSumOfTotalPayable())* 100 || 0;
    }else {
      rslt = this.incomeTaxInsurArrayConsol.get('consolidated').get('overridingCommission').value/(
        this.getTotalNonPayableCol('consolidated', this.incomeTaxInsurArrayConsol) + this.getTotalPayableCol('consolidated', this.incomeTaxInsurArrayConsol))* 100 || 0;
      
    }
    return rslt;

    // return this.roundVal(this.captionGroup.get('radioOption').value==='MTH'? (this.getSumOfCurrRow('overridingCommission')/this.aveInsurForm?.get('eligibleMthCount').value)/
    // ((this.getSumOfTotalNonPayable() + this.getSumOfTotalPayable())/this.aveInsurForm?.get('eligibleMthCount').value) * 100:
