  computeConsolidatedAvgTotalPayable(ctrlName: string, formArr: FormGroup): number
  {
    let avgSum = 0;
    avgSum=
    Number(formArr.get(ctrlName).get('firstYrCommission').value /this.aveInsurForm?.get('eligibleMthCount').value)
    +  Number(formArr.get(ctrlName).get('singlePremium').value/this.aveInsurForm?.get('eligibleMthCount').value)
    +  Number(formArr.get(ctrlName).get('prdCommission').value/this.aveInsurForm?.get('eligibleMthCount').value)
    +  Number(formArr.get(ctrlName).get('generalInsurance').value/this.aveInsurForm?.get('eligibleMthCount').value)
    +  Number(formArr.get(ctrlName).get('otherCommission').value/this.aveInsurForm?.get('eligibleMthCount').value)
    +  Number(formArr.get(ctrlName).get('prdBonus').value/this.aveInsurForm?.get('prdnBonusDivisor').value)

    return avgSum;
  }

  computeConsolidatedAvgTotalNonPayable(ctrlName: string, formArr: FormGroup): number
  {
    let avgSum = 0;
    avgSum=
    Number(formArr.get(ctrlName).get('renewalCommission').value /this.colMthRange.length)
    +  Number(formArr.get(ctrlName).get('overridingCommission').value/this.colMthRange.length)
    +  Number(formArr.get(ctrlName).get('otherNonPayable').value/this.colMthRange.length);

    return avgSum;
  }

