  getTotalAmtPayable(): number{
    let amtPayable = 0;
    if(this.captionGroup.get('radioOption').value==='MTH'){
      amtPayable = this.aveInsurForm.get('footerGroup.claimNip').value /100 *
      this.getSumOfTotalPayable()/this.aveInsurForm?.get('eligibleMthCount').value + 0.42 * this.getSumOfCurrRow('firstYrCommission')/this.aveInsurForm?.get('eligibleMthCount').value;

    }else {
      amtPayable = this.aveInsurForm.get('footerGroup.claimNip').value /100 * this.computeConsolidatedAvgTotalPayable('consolidated',this.incomeTaxInsurArrayConsol) + 0.42 *
      this.incomeTaxInsurArrayConsol.get('consolidated').get('firstYrCommission').value /this.aveInsurForm?.get('eligibleMthCount').value
    }
    return this.roundVal(amtPayable);
  }

  getTotalAmtPayableDesc(): string{
    let amtPayableDesc = "";

    if(this.captionGroup.get('radioOption').value==='MTH'){
      amtPayableDesc = `${String(this.aveInsurForm.get('footerGroup.claimNip').value)} % * TPC(${String(this.roundVal2Dec(this.getSumOfTotalPayable()/this.aveInsurForm?.get('eligibleMthCount').value))})
      + 42% * FYC (${this.roundVal2Dec(this.getSumOfCurrRow('firstYrCommission')/this.aveInsurForm?.get('eligibleMthCount').value)}  `;
    }else{
      amtPayableDesc = `${String(this.aveInsurForm.get('footerGroup.claimNip').value)} % * TPC(${String(this.roundVal2Dec(this.computeConsolidatedAvgTotalPayable('consolidated',this.incomeTaxInsurArrayConsol)))}) 
      + 42% * FYC ( ${String(this.roundVal2Dec(this.incomeTaxInsurArrayConsol.get('consolidated').get('firstYrCommission').value /this.aveInsurForm?.get('eligibleMthCount').value))}) `;

    }
    return amtPayableDesc;

