  isTradeIncomeMorethanTotalCommision(): boolean{
    let irasRecord;
    //if actual
    if(this.irasDetails?.filter((x:any)=> x.basis === "2").length>0){
      irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "2")[0];
    //if provisional
    }else if(this.irasDetails?.filter((x:any)=> x.basis === "1").length>0){
      irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "1")[0];
    }
    if(this.aveInsurForm.get('captionGroup.radioOption').value==='MTH'){
      return irasRecord?.tradeIncome> (this.getSumOfTotalNonPayable() + this.getSumOfTotalPayable());
    }else{
      return irasRecord?.tradeIncome > (this.getTotalNonPayableCol('consolidated', this.incomeTaxInsurArrayConsol) + this.getTotalPayableCol('consolidated', this.incomeTaxInsurArrayConsol));
    }
  }

  isWorkingDataTotalPayableNotEqualItemApprAmt(): boolean{
    const claimItemList = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    let itxItem = null;
    let annualtotalPayable = 0;
    if(claimItemList.length>0){
      itxItem = claimItemList?.controls.find(x=> x.get('itemType').value === 'AVE');
      const apprAmt = itxItem?.get('apprAmt')?.value;
      console.log("appr amt in ave is ", apprAmt);
      if(this.aveInsurForm.get('captionGroup.radioOption').value==='MTH'){
        annualtotalPayable = this. getSumOfTotalPayable();
      }else {
        annualtotalPayable = this.getTotalPayableCol('consolidated', this.incomeTaxInsurArrayConsol);
      }
      return annualtotalPayable !== apprAmt;

    }else return false;
  }

