  exportAddMthly(yrmthFG: any, dataList: InsurITXSubWD[], subWD: InsurITXSubWD, subsetYrmth: any[], dtoList: any[]){
    //mthly
    subsetYrmth.forEach(key=>{
      yrmthFG = this.aveInsurForm.get('incomeTaxInsurArrayMthly').get(key);
      subWD = {
        yrmth: yrmthFG.get("yrmthReCon").value,
        firstYrCommission: yrmthFG.get("firstYrCommission").value,
        singlePremium: yrmthFG.get("singlePremium").value,
        prdCommission:yrmthFG.get("prdCommission").value,
        generalInsurance: yrmthFG.get("generalInsurance").value,
        otherCommission: yrmthFG.get("otherCommission").value,
        prdBonus: yrmthFG.get("prdBonus").value,
        renewalCommission: yrmthFG.get("renewalCommission").value,
        overridingCommission: yrmthFG.get("overridingCommission").value,
        otherNonPayable: yrmthFG.get("otherNonPayable").value,
      }
      dataList.push(subWD);
      }
    );

     dataList.forEach(x=>{
        dtoList[0][x.yrmth] = x.firstYrCommission;
        dtoList[1][x.yrmth] = x.singlePremium;
        dtoList[2][x.yrmth] = x.prdCommission;
        dtoList[3][x.yrmth] = x.generalInsurance;
        dtoList[4][x.yrmth] = x.otherCommission;
        dtoList[5][x.yrmth] = x.prdBonus;
        dtoList[6][x.yrmth] = x.firstYrCommission + x.singlePremium + x.prdCommission + x.generalInsurance + x.otherCommission + x.prdBonus;
        dtoList[7][x.yrmth] = x.renewalCommission;
        dtoList[8][x.yrmth] = x.overridingCommission;
        dtoList[9][x.yrmth] = x.otherNonPayable;
        dtoList[10][x.yrmth] = x.renewalCommission + x.overridingCommission + x.otherNonPayable;
        dtoList[11][x.yrmth] = +dtoList[6][x.yrmth] + +dtoList[10][x.yrmth]


 
    });
    dtoList[0]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.firstYrCommission}));
    dtoList[1]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.singlePremium}));
    dtoList[2]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.prdCommission}));
    dtoList[3]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.generalInsurance}));
    dtoList[4]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.otherCommission}));
    dtoList[5]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.prdBonus}));
    dtoList[6]['mthAccount'] =  +dtoList[0]['mthAccount'] + +dtoList[1]['mthAccount'] + +dtoList[2]['mthAccount'] + +dtoList[3]['mthAccount'] + +dtoList[4]['mthAccount'] + +dtoList[5]['mthAccount'];//total payable commission
    dtoList[7]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.renewalCommission}));
    dtoList[8]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.overridingCommission}));
    dtoList[9]['mthAccount'] = this.getSumByField(dataList.map(x=> {return x.otherNonPayable}));
    dtoList[10]['mthAccount'] = +dtoList[7]['mthAccount'] + +dtoList[8]['mthAccount'] + +dtoList[9]['mthAccount'];
    dtoList[11]['mthAccount'] = +dtoList[6]['mthAccount'] + +dtoList[10]['mthAccount'];


    dtoList[0]['aveMthAccount'] = dtoList[0]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[1]['aveMthAccount'] = dtoList[1]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[2]['aveMthAccount'] = dtoList[2]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[3]['aveMthAccount'] = dtoList[3]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[4]['aveMthAccount'] = dtoList[4]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[5]['aveMthAccount'] = dtoList[5]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[6]['aveMthAccount'] = dtoList[6]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[7]['aveMthAccount'] = dtoList[7]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[8]['aveMthAccount'] = dtoList[8]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[9]['aveMthAccount'] = dtoList[9]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[10]['aveMthAccount'] = dtoList[10]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;
    dtoList[11]['aveMthAccount'] = dtoList[11]['mthAccount'] / this.aveInsurForm?.get('eligibleMthCount').value;

  }

  getSumByField(fieldArray: number[]): number{
    const initValue = 0;
    const sum = fieldArray.reduce((accumulator, currentValue) => accumulator + currentValue, initValue);
    return sum;
  }

    //allows input on need basis
    hasValidAuthorityAndStation(): boolean{
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_VERIFIER) && 
        this.station === NSPAY2_STATION.STATION_VERIFICATION){
          return true;
        }
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
      this.station === NSPAY2_STATION.STATION_CERTIFICATION) {
        return false;
      }
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
      this.station === NSPAY2_STATION.STATION_APPROVAL) {
        return false;
      }
      return false;
    }
}
