import { Component, Inject, LOCALE_ID, OnInit, ViewChild } from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { Option, IQueryApproveVES, RemarksList, SupportingDocsList, NsMan, Nric } from '../model/payroll.model';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { PayrollApprovalService } from '../service/payroll-approval.service';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Table } from 'primeng/table';
import { FileUploadService } from 'app/shared/service/file-upload.service';
import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { NSPAY2_REFTYPE, NSPAY2_REFCODE } from 'app/nspay2.constants';
import { ActivatedRoute, Router } from '@angular/router';
import { CABComponent } from 'app/enquiry/caution-alert-block/cab.component';
import { RemarkValidator } from '../../../../app/shared/validator/remark.directive'
import { IcCodeService } from 'app/ng-connect';

@Component({
  selector: 'n2-approve-payroll-new',
  templateUrl: './approve-payroll-new.component.html',
  styleUrls: ['./approve-payroll-new.component.scss']
})

export class ApprovePayrollNewComponent implements OnInit {

  readonly NSPAY2_REFTYPE = NSPAY2_REFTYPE;
  readonly NSPAY2_REFCODE = NSPAY2_REFCODE;

  remarksForm: FormGroup = this.fb.group({
    remarks: new FormControl(null),
  }, {
    validators: RemarkValidator.rejRemark("remarks")
  });

  tableForm: FormGroup = this.fb.group({
    startTime: new FormControl(null),
    endTime: new FormControl(null),
    payableAmt: new FormControl(null)
  }, {});

  nsman: NsMan[] = [];
  approvals: IQueryApproveVES[] = [];
  remarkslist: RemarksList[] = [];
  docs: SupportingDocsList[] = [];


  validIndOptions: any[] = [];
  servicePayEligOptions: any[] = [];
  topUpEligOptions: any[] = [];
  validIndMap = new Map<string, string>();
  servicePayEligMap = new Map<string, string>();
  topUpEligMap = new Map<string, string>();

  displayDropdown = false;

  selectedValid: string;
  selectedServicePayElig: string;
  selectedTopUpElig: string;

  awardCodes = new Map<string, string>();
  allowanceCodes = new Map<string, string>();
  attendanceCodes = new Map<string, string>();

  payrollDesc: string;

  noApproval: string;
  noTransaction: string;
  pendingApproval: string;

  payrollStartDt: string;

  refType: string;
  refCode: string;
  requestId: string;
  requestnric: string[] = [];

  disableButton = true;
  displayNsmanCABDialog: any = { display: false };
  confirmationDialog: any = { display: false };
  confirmationRDialog: any = { display: false };
  rejectionDialog: any;

  @ViewChild("NsmanCAB", { static: true })
  private NsmanCAB: CABComponent;

  constructor(
    private fb: FormBuilder,
    private toastService: CustomToastService,
    private n2httpService: Ns2HttpService,
    private nspConfirmService: NspConfirmationService,
    private payrollApprovalService: PayrollApprovalService,
    private dateUtilsService: DateUtilsService,
    private exportUtilsService: ExportUtilsService,
    private fileUploadService: FileUploadService,
    private route: ActivatedRoute,
    private router: Router,
    private codeService: IcCodeService,
    @Inject(LOCALE_ID) protected localeId: string,
  ) { }

  ngOnInit(): void {
    this.requestId = this.route.snapshot.paramMap.get("id");
    this.loadCodes();
    this.loadData();
  }

  loadData(): void {

    this.payrollApprovalService.getPayrollRecord(this.requestId).subscribe(
      (res: HttpResponse<any>) => {
        this.approvals.push(res.body.data);
        this.requestnric.push(res.body.data.nric);
        console.log(res);
        this.remarkslist = res.body.data.remarks;
        this.docs = res.body.data.supportingDocs;
        this.refCode = this.approvals[0].refCode;
        this.refType = this.approvals[0].refType;

        this.selectedValid = this.validIndMap.get(res.body.data.validInd);
        this.setElig(res);
        this.displayDropdown = true;

        this.setPayrollDesc(this.refType, this.refCode);

        this.getDetails();
        this.tableForm.get('startTime').patchValue(this.approvals[0].payrollStartDt.substring(11, 16).replace(':', ''));
        this.tableForm.get('endTime').patchValue(this.approvals[0].payrollEndDt.substring(11, 16).replace(':', ''));
      },
      (error: HttpErrorResponse) => {
        console.log(error);
        this.toastService.error(error.error.error_desc);
        this.router.navigate(['']);
      }
    );
  }

  loadCodes(): void {
    // this.codeService.gets('PRL_SP_ELIG,PRL_NST_ELIG,PRL_PAYROLL_VALID').subscribe((codes: any[]) => {
    //     console.log(codes);
    //   });

    this.payrollApprovalService.getCodes('PRL_SP_ELIG,PRL_NST_ELIG,PRL_PAYROLL_VALID').subscribe((codes: HttpResponse<any>) => {
      console.log(codes);

      this.servicePayEligOptions = codes.body[0];
      this.servicePayEligOptions.forEach((codeDesc) => {
        this.servicePayEligMap.set(codeDesc.codeId, codeDesc.label['en-US']);
      });

      this.topUpEligOptions = codes.body[1];
      this.topUpEligOptions.forEach((codeDesc) => {
        this.topUpEligMap.set(codeDesc.codeId, codeDesc.label['en-US']);
      });

      this.validIndOptions = codes.body[2];
      this.validIndOptions.forEach((codeDesc) => {
        this.validIndMap.set(codeDesc.codeId, codeDesc.label['en-US']);
      })
    });

    this.payrollApprovalService.getCodes('PRL_AWD_CODE,PRL_ALLW_CODE,PRL_ATTD_CODE')
      .subscribe((codes: HttpResponse<any>) => {
        console.log(codes);

        codes.body[0].forEach((codeDesc: any) => {
          this.awardCodes.set(codeDesc.codeId, codeDesc.label['en-US']);
        });

        codes.body[1].forEach((codeDesc: any) => {
          this.allowanceCodes.set(codeDesc.codeId, codeDesc.label['en-US']);
        });

        codes.body[2].forEach((codeDesc: any) => {
          this.attendanceCodes.set(codeDesc.codeId, codeDesc.label['en-US']);
        });

        console.log(this.awardCodes);
        console.log(this.allowanceCodes);
        console.log(this.attendanceCodes);


      });
  }

  setElig(data: HttpResponse<any>): void {
    const spElig = data.body.data.servicePayEligInd;
    const tpElig = data.body.data.topUpEligInd;

    if (spElig !== null) {
      this.selectedServicePayElig = spElig;
    } else {
      this.selectedServicePayElig = this.servicePayEligOptions[2].codeId;
    }

    if (tpElig !== null) {
      this.selectedTopUpElig = tpElig;
    } else {
      this.selectedTopUpElig = this.topUpEligOptions[2].codeId;
    }
  }

  setPayrollDesc(refType: any, refCode: any): void {
    console.log(refType);
    console.log(refCode);

    if (refType === NSPAY2_REFTYPE.AWARD) {
      this.payrollDesc = this.awardCodes.get(refCode);
    } else if (refType === NSPAY2_REFTYPE.ALLOWANCE) {
      this.payrollDesc = this.allowanceCodes.get(refCode);
    } else if (refType === NSPAY2_REFTYPE.ATTENDANCE) {
      this.payrollDesc = this.attendanceCodes.get(refCode);
    }

    console.log(this.payrollDesc);

  }

  getRecords() {
    this.payrollApprovalService.getAllPayrollRecord(this.requestnric).subscribe(
      (res: HttpResponse<any>) => {
        this.approvals = res.body.data;
      },
    );
  }

  getDetails() {
    this.payrollApprovalService.getNsman(this.requestnric[0]).subscribe(
      (res: HttpResponse<any>) => {
        this.nsman.push(res.body.data);
        console.log(this.requestnric)
      },
      (error: HttpErrorResponse) => {
        console.log(error.message);
      }
    );
  }

  downloadDoc(docId: string): void {
    if (docId) {
      this.fileUploadService.downloadTxnId(docId).subscribe(
        res => {
          if (res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type') });
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {
          console.log(error);
          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }

  downloadAll(): void {
    for (let i = 0; i < this.docs.length; i++) {
      const docId = this.docs[i].documentId;
      this.fileUploadService.downloadTxnId(docId).subscribe(
        res => {
          if (res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type') });
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {
          console.log(error);
          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }

  backInbox(): void {
    //  
  }

  nextTransaction(): void {
    //  
  }

  nextSameNRIC(): void {
    // this.router.navigate(['payroll/approval/approve-new/' + this.requestId], { relativeTo: this.route });
  }

  nextSameType(): void {
    //
  }

  onOpen(dialog: any): void {
    dialog.display = true;
  }

  onSubmit2(event: Event, key: string, dialog: any): void {
    const request = {
      requestId: this.requestId,
      approve: false,
      rejectReason: this.remarksForm.get('remarks').value as string,
    }

    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {

      // if (this.remarksForm.get('remarks').value.length === 0) {
      //   this.toastService.error("Rejection Remark is required");
      // }
      // else if (this.remarksForm.get('remarks').value.length > 2000) {
      //   this.toastService.error("Rejection Remarks cannot be greater than 2000 characters");
      // }
      // else {
      this.payrollApprovalService.approve(request).subscribe(
        (res: HttpResponse<any>) => {
          if (res.body) {
            if (this.refType === "AWARDS") {
              this.toastService.success("Rejected Award record");
            } else if (this.refType === "ALLOWANCES") {
              this.toastService.success("Rejected Allowance record");
            } else if (this.refType === "ATTENDANCE") {
              this.toastService.success("Rejected Attendance record");
            }
          }
          else {
            this.toastService.warn("Unsuccessful");
          }
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error.error_desc);
        }
      );
      // }
    })
  }

  onSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      const request = {
        requestId: this.requestId,
        approve: true,

      }
      console.log(request);
      setTimeout(() => {
        this.payrollApprovalService.approve(request).subscribe(
          (res: HttpResponse<any>) => {
            if (res.body) {
              if (this.refType === "AWARDS") {
                this.toastService.success("Successfully approved Award record");
              } else if (this.refType === "ALLOWANCES") {
                this.toastService.success("Successfully approved Allowance record");
              } else if (this.refType === "ATTENDANCE") {
                this.toastService.success("Successfully approved Attendance record");
              }
            }
            else {
              this.toastService.warn("Unsuccessful");
            }
          },
          (error: HttpErrorResponse) => {
            this.toastService.error(error.error.error_desc);
          }
        );
      }, 1000);
    })
  }

}
<p-panel>
    <ng-template pTemplate="header">
        <div [(ngModel)]="noApproval">For Approval (<span>X{{noApproval}}</span>) </div>
    </ng-template>
    <ng-template pTemplate="icons">
        <button pButton type="button" class="p-button-primary" label="Back to Inbox" (click)="backInbox()"></button>
        <button pButton type="button" class="p-button-primary" label="Next Transaction"
            (click)="nextTransaction()"></button>
        <button pButton type="button" class="p-button-primary" label="Next Same Type" (click)="nextSameType()"></button>
        <button pButton type="button" class="p-button-primary" label="Next Same NRIC" (click)="nextSameNRIC()"></button>
    </ng-template>
</p-panel>

<p-messages severity="info">
    <ng-template pTemplate>
        <div [(ngModel)]="noApproval" [(ngModel)]="noTransaction" [(ngModel)]="pendingApproval">
            <span>X{{noTransaction}}</span> - <span>{{noApproval}}</span> <span>X Pending Approval(s) for same
                NRIC</span>
        </div>
    </ng-template>
</p-messages>

<div class="grid">
    <div class="col-6">
        <div class="grid">
            <div class="col-12">
                <p-panel header='Remarks'>
                    <div *ngFor="let item of remarkslist">
                        <div>{{ item.remark }}</div>
                    </div>
                </p-panel>
            </div>
        </div>
    </div>
    <div class="col-6">
        <div class="grid">
            <div class="col-12">
                <p-panel header='Supporting Document'>
                    <ng-template pTemplate="icons">
                        <button pButton label="Download All" class="p-button-link underline text-blue-500"
                            (click)="downloadAll()"></button>
                    </ng-template>

                    <div *ngFor="let item of docs">
                        <td><a class="underline text-blue-500"
                                (click)="downloadDoc(item.documentId)">{{item.fileName}}</a>
                    </div>
                </p-panel>
            </div>
        </div>
    </div>
</div>
<br>
<div style="margin-bottom:10px">
    <p-panel class="nsmanInfo">
        <ng-template pTemplate="header">
            <div [(ngModel)]="refType">Payroll Details (<span>{{refType | titlecase}}</span>) </div>
        </ng-template>
        <ng-template pTemplate="body">
            <div *ngFor="let record of nsman">
                <div style="font-size: 35px" class="inline-block text-center font-semibold">{{record.nric}}
                    {{record.name}}
                </div>
                <div style="font-size: 18px" class="inline-block w-7rem text-center font-medium">{{record.rank}}
                    ({{record.gender}})
                </div>
                <div style="font-size: 18px" class="inline-block w-7rem text-center font-medium">
                    <div class="grid">
                        <div>CAB: </div>
                        <div> <button pButton type="button" class="p-button-link text-blue"
                                (click)="onOpen(displayNsmanCABDialog)"> N
                            </button>
                        </div>
                    </div>
                </div>
                <div class="inline-block w-20rem text-center">Payroll CPF: X</div>
                <div class="inline-block w-14rem text-center">TRG Start CPF: X</div>
            </div>
        </ng-template>
    </p-panel>
</div>

<p-table [value]="approvals" styleClass="p-datatable-gridlines" responsiveLayout="scroll">
    <ng-template pTemplate="header">
        <tr>
            <th scope="col" style="width: 5%">Description</th>
            <th scope="col" style="width: 5%">Start Date</th>
            <th scope="col" style="width: 5%"> Start Time</th>
            <th scope="col" style="width: 5%"> End Date</th>
            <th scope="col" style="width: 5%"> End Time</th>
            <th scope="col" style="width: 5%"> Amt</th>
            <th scope="col" style="width: 5%"> Service Pay Eligibility</th>
            <th scope="col" style="width: 5%"> Top Up Eligibility</th>
            <th scope="col" style="width: 5%"> Valid</th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-record let-i="rowIndex">
        <tr>
            <td>{{ payrollDesc | defaultString }}</td>
            <td>{{ record.payrollStartDt | date: 'dd-MM-yyyy' }}</td>
            <td>
                <form [formGroup]="tableForm">
                    <input type="text" pInputText [readonly]="true" class="form-control" id="startTime"
                        formControlName="startTime"
                        class="text-base text-color surface-overlay p-2 border-1 border-solid surface-border border-round appearance-none outline-none focus:border-primary w-full">
                </form>
            </td>
            <td>
                {{ record.payrollEndDt | date: 'dd-MM-yyyy' }}</td>
            <td>
                <form [formGroup]="tableForm">
                    <input type="text" pInputText [readonly]="true" class="form-control" id="endTime"
                        formControlName="endTime"
                        class="text-base text-color surface-overlay p-2 border-1 border-solid surface-border border-round appearance-none outline-none focus:border-primary w-full">
                </form>
            </td>

            <td>
                {{ record.payableAmt | number:'1.2-2' }}
            </td>
            <td>
                <p-dropdown [options]='servicePayEligOptions' optionLabel="label.en-US" optionValue="codeId"
                    [(ngModel)]="selectedServicePayElig" autoWidth="false" [style]="{'width':'100%'}"
                    *ngIf="displayDropdown" [readonly]="true"></p-dropdown>
            </td>
            <td>
                <p-dropdown [options]='topUpEligOptions' optionLabel="label.en-US" optionValue="codeId"
                    [(ngModel)]="selectedTopUpElig" autoWidth="false" [style]="{'width':'100%'}" *ngIf="displayDropdown"
                    [readonly]="true"></p-dropdown>
            </td>
            <td>
                <p-dropdown [options]='validIndOptions' optionLabel="label.en-US" optionValue="codeId"
                    [(ngModel)]="selectedValid" autoWidth="false" [style]="{'width':'100%'}" *ngIf="displayDropdown"
                    [readonly]="true"></p-dropdown>
            </td>
        </tr>
    </ng-template>
</p-table>
<p></p>
<div class="flex justify-content-end align-items-center">
    <div>
        <button pButton type="button" label="Reject" class="p-button-outlined"
            (click)="onOpen(confirmationRDialog);"></button>

        <p-confirmPopup key='approve'></p-confirmPopup>
        <button pButton label='Approve' class="p-button-success"
            (click)="onSubmit($event, 'approve', confirmationDialog)"></button>
    </div>
</div>

<div class="flex align-items-center justify-content-center" id="tablee"
    *ngIf="refType === NSPAY2_REFTYPE.ALLOWANCE && refCode === NSPAY2_REFCODE.OTHER">
    <p-table [value]="approvals" styleClass="p-datatable-gridlines">
        <ng-template pTemplate="header">
            <tr>
                <th scope="col" style="width: 20%"> Component </th>
                <th scope="col" style="width: 20%"> Amount </th>
            </tr>
        </ng-template>
        <ng-template pTemplate="body" let-record let-i="rowIndex">
            <tr>
                <td>
                    Service Pay
                </td>
                <td>
                    {{ record.servicePayAmt | number:'1.2-2' }}
                </td>
            </tr>
            <tr>
                <td>
                    NS Top Up
                </td>
                <td>
                    {{ record.nsTopUpAmt | number:'1.2-2' }}
                </td>
            </tr>
        </ng-template>
    </p-table>
</div>

<p-dialog [(visible)]='confirmationRDialog.display' [breakpoints]="{'960px': '75vw'}" [style]="{width: '50vw'}"
    [draggable]='false' [resizable]='false' [modal]='true'>
    <ng-template pTemplate="header">
        <div [(ngModel)]="refType"><span>{{refType | titlecase}}</span> Rejection </div>
    </ng-template>
    <form [formGroup]="remarksForm" #rejectionDialog>
        <label for="remarks">Rejection Remarks</label>
        <textarea type="text" pInputText id="remarks" formControlName="remarks" rows="4"
            class="text-base text-color surface-overlay p-2 border-1 border-solid surface-border border-round appearance-none outline-none focus:border-primary w-full"></textarea>
    </form>
    <p></p>
    <div class="flex justify-content-end align-items-center">
        <div>
            <button pButton type="button" label="Cancel" class="p-button-danger"
                (click)='confirmationRDialog.display=false'></button>

            <p-confirmPopup key='reject'></p-confirmPopup>
            <button pButton label='Submit' class="p-button-success" [disabled]="!remarksForm.valid"
                (click)="onSubmit2($event, 'reject', confirmationDialog)"></button>
        </div>
    </div>
</p-dialog>
